import React, { Component, ErrorInfo, ReactNode } from 'react';
import { AlertTriangle, RefreshCw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { config } from '@/config/environment';

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
  onError?: (error: Error, errorInfo: ErrorInfo) => void;
}

interface State {
  hasError: boolean;
  error: Error | null;
  errorInfo: ErrorInfo | null;
}

export class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = {
      hasError: false,
      error: null,
      errorInfo: null,
    };
  }

  static getDerivedStateFromError(error: Error): State {
    return {
      hasError: true,
      error,
      errorInfo: null,
    };
  }

  componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    this.setState({
      error,
      errorInfo,
    });

    // Log error to console in development
    if (config.isDevelopment) {
      console.error('ErrorBoundary caught an error:', error, errorInfo);
    }

    // Call custom error handler if provided
    if (this.props.onError) {
      this.props.onError(error, errorInfo);
    }

    // In production, you might want to send this to an error reporting service
    if (config.isProduction) {
      // Example: Send to error reporting service
      // errorReportingService.captureException(error, { extra: errorInfo });
    }
  }

  handleRetry = () => {
    this.setState({
      hasError: false,
      error: null,
      errorInfo: null,
    });
  };

  render() {
    if (this.state.hasError) {
      // Custom fallback UI
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Default error UI
      return (
        <div className="min-h-screen flex items-center justify-center p-4 bg-gray-50">
          <Card className="w-full max-w-md">
            <CardHeader className="text-center">
              <div className="mx-auto mb-4 flex h-12 w-12 items-center justify-center rounded-full bg-red-100">
                <AlertTriangle className="h-6 w-6 text-red-600" />
              </div>
              <CardTitle className="text-xl font-semibold text-gray-900">
                Something went wrong
              </CardTitle>
              <CardDescription className="text-gray-600">
                We're sorry, but something unexpected happened. Please try refreshing the page.
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <Button 
                onClick={this.handleRetry} 
                className="w-full"
                variant="default"
              >
                <RefreshCw className="mr-2 h-4 w-4" />
                Try Again
              </Button>
              
              <Button 
                onClick={() => window.location.reload()} 
                className="w-full"
                variant="outline"
              >
                Refresh Page
              </Button>

              {config.isDevelopment && this.state.error && (
                <details className="mt-4 p-3 bg-gray-100 rounded-md text-sm">
                  <summary className="cursor-pointer font-medium text-gray-700 mb-2">
                    Error Details (Development)
                  </summary>
                  <div className="space-y-2">
                    <div>
                      <strong>Error:</strong>
                      <pre className="mt-1 text-xs text-red-600 whitespace-pre-wrap">
                        {this.state.error.toString()}
                      </pre>
                    </div>
                    {this.state.errorInfo && (
                      <div>
                        <strong>Component Stack:</strong>
                        <pre className="mt-1 text-xs text-gray-600 whitespace-pre-wrap">
                          {this.state.errorInfo.componentStack}
                        </pre>
                      </div>
                    )}
                  </div>
                </details>
              )}
            </CardContent>
          </Card>
        </div>
      );
    }

    return this.props.children;
  }
}

// API Error Boundary specifically for API-related errors
interface ApiErrorBoundaryProps {
  children: ReactNode;
  onRetry?: () => void;
}

export const ApiErrorBoundary: React.FC<ApiErrorBoundaryProps> = ({ 
  children, 
  onRetry 
}) => {
  return (
    <ErrorBoundary
      fallback={
        <div className="flex flex-col items-center justify-center p-8 text-center">
          <AlertTriangle className="h-12 w-12 text-red-500 mb-4" />
          <h3 className="text-lg font-semibold text-gray-900 mb-2">
            Connection Error
          </h3>
          <p className="text-gray-600 mb-4">
            Unable to connect to the server. Please check your internet connection and try again.
          </p>
          <div className="space-x-2">
            {onRetry && (
              <Button onClick={onRetry} variant="default">
                <RefreshCw className="mr-2 h-4 w-4" />
                Retry
              </Button>
            )}
            <Button 
              onClick={() => window.location.reload()} 
              variant="outline"
            >
              Refresh Page
            </Button>
          </div>
        </div>
      }
    >
      {children}
    </ErrorBoundary>
  );
};

// Hook for handling API errors in components
export const useErrorHandler = () => {
  const handleError = (error: any) => {
    console.error('API Error:', error);
    
    // You can customize error handling based on error type
    if (error.response?.status === 401) {
      // Unauthorized - redirect to login
      window.location.href = '/login';
    } else if (error.response?.status === 403) {
      // Forbidden - show access denied message
      // You could dispatch a global notification here
    } else if (error.response?.status >= 500) {
      // Server error - show server error message
      // You could dispatch a global notification here
    }
    
    // Return user-friendly error message
    if (error.response?.data?.message) {
      return error.response.data.message;
    }
    
    if (error.message) {
      return error.message;
    }
    
    return 'An unexpected error occurred. Please try again.';
  };

  return { handleError };
};

export default ErrorBoundary;

import React, { useState, useEffect, useRef } from 'react'
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Badge } from '@/components/ui/badge'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert'
import { Separator } from '@/components/ui/separator'
import { CheckCircle, Clock, AlertTriangle, XCircle, RefreshCw, User } from 'lucide-react'
import { api } from '@/services/unifiedApi'
import { LeaveRequest, SalaryAdvanceRequest } from '@/types/types'
import { useAuth } from '@/contexts/AuthContext'

// Fix badge array typing
interface BadgeItem {
  key: string;
  label: string;
  variant: "default" | "destructive" | "outline" | "secondary";
}

interface WorkflowDashboardProps {
  className?: string;
}

const WorkflowDashboard: React.FC<WorkflowDashboardProps> = ({ className }) => {
  const { user } = useAuth();
  const [leaveRequests, setLeaveRequests] = useState<LeaveRequest[]>([]);
  const [salaryAdvances, setSalaryAdvances] = useState<SalaryAdvanceRequest[]>([]);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('leave');
  const [error, setError] = useState<string | null>(null);
  const pollingRef = useRef<NodeJS.Timeout | null>(null);

  // Properly typed badge arrays
  const leaveStatusBadges: BadgeItem[] = [
    { key: 'pending_ops_initial', label: 'Pending Operations Review', variant: 'default' },
    { key: 'forwarded_to_hr', label: 'Forwarded to HR', variant: 'default' },
    { key: 'hr_rejected', label: 'HR Rejected', variant: 'destructive' },
    { key: 'hr_approved', label: 'HR Approved', variant: 'destructive' },
    { key: 'ops_final_approved', label: 'Final Approved', variant: 'default' },
    { key: 'ops_final_rejected', label: 'Final Rejected', variant: 'destructive' }
  ];

  const salaryAdvanceStatusBadges: BadgeItem[] = [
    { key: 'pending_ops_initial', label: 'Pending Operations Review', variant: 'default' },
    { key: 'forwarded_to_hr', label: 'Forwarded to HR', variant: 'default' },
    { key: 'hr_rejected', label: 'HR Rejected', variant: 'destructive' },
    { key: 'hr_approved', label: 'HR Approved', variant: 'destructive' },
    { key: 'ops_final_approved', label: 'Final Approved', variant: 'default' },
    { key: 'disbursed', label: 'Disbursed', variant: 'default' },
    { key: 'ops_final_rejected', label: 'Final Rejected', variant: 'destructive' }
  ];

  // Retry logic for network errors
  const loadData = async (showLoading = false, retries = 2) => {
    if (!user) return;
    if (showLoading) setLoading(true);
    setError(null);
    try {
      const role = user.role === 'OPERATIONS_MANAGER' ? 'operations' : 
                   user.role === 'HR_MANAGER' ? 'hr' : 'employee';
      const [leaves, advances] = await Promise.all([
        api.data.getLeaveRequests(role, { employeeId: user.id }),
        api.data.getSalaryAdvanceRequests(role, { employeeId: user.id })
      ]);
      setLeaveRequests(leaves);
      setSalaryAdvances(advances);
    } catch (err: any) {
      if (err?.code === 'ERR_NETWORK' && retries > 0) {
        // Wait 2 seconds before retrying
        setTimeout(() => loadData(showLoading, retries - 1), 2000);
        return;
      }
      if (err?.code === 'ERR_NETWORK') {
        setError('Cannot connect to the server. Please check your network connection or try again later.');
      } else {
        setError('Failed to load data. Please try again.');
      }
      console.error('Error loading data:', err);
    } finally {
      if (showLoading) setLoading(false);
    }
  };

  // Polling effect
  useEffect(() => {
    if (!user) return;
    loadData(true); // Initial load with spinner
    pollingRef.current = setInterval(() => {
      loadData(false); // Silent polling
    }, 60000); // Changed from 10 seconds to 60 seconds
    return () => {
      if (pollingRef.current) clearInterval(pollingRef.current);
    };
  }, [user]);

  const handleLeaveAction = async (requestId: string, action: string) => {
    if (!user) return;
    
    setLoading(true);
    try {
      const role = user.role === 'OPERATIONS_MANAGER' ? 'operations' : 'hr';
      
      switch (action) {
        case 'forward_to_hr':
          await api.data.forwardLeaveRequestToHR(requestId, user.id, 'Forwarding for eligibility review');
          break;
        case 'hr_approve':
          await api.data.reviewLeaveRequestHR(requestId, user.id, 'eligible', 'Employee is eligible for leave');
          break;
        case 'hr_reject':
          await api.data.reviewLeaveRequestHR(requestId, user.id, 'not_eligible', 'Employee not eligible for leave');
          break;
        case 'ops_approve':
          await api.data.finalDecisionLeaveRequest(requestId, user.id, 'approved', 'Final approval granted');
          break;
        case 'ops_reject':
          await api.data.finalDecisionLeaveRequest(requestId, user.id, 'rejected', 'Final approval denied');
          break;
      }
      
      await loadData();
    } catch (error) {
      console.error('Error processing leave action:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSalaryAdvanceAction = async (requestId: number, action: string) => {
    if (!user) return;
    
    setLoading(true);
    try {
      switch (action) {
        case 'forward_to_hr':
          await api.data.forwardSalaryAdvanceToHR(requestId, user.id, 'Forwarding for eligibility check');
          break;
        case 'hr_approve':
          await api.data.reviewSalaryAdvanceHR(requestId, user.id, 'eligible', 'Employee eligible for advance', {
            currentSalary: 30000,
            existingAdvances: 0,
            maxAllowableAdvance: 15000,
            employmentTenure: 12,
            creditworthiness: 'good'
          });
          break;
        case 'hr_reject':
          await api.data.reviewSalaryAdvanceHR(requestId, user.id, 'not_eligible', 'Employee not eligible for advance');
          break;
        case 'ops_approve':
          await api.data.finalDecisionSalaryAdvance(requestId, user.id, 'approved', 'Final approval granted');
          break;
        case 'ops_reject':
          await api.data.finalDecisionSalaryAdvance(requestId, user.id, 'rejected', 'Final approval denied');
          break;
        case 'disburse':
          await api.data.disburseSalaryAdvance(requestId, user.id);
          break;
      }
      
      await loadData();
    } catch (error) {
      console.error('Error processing salary advance action:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status: string, type: 'leave' | 'salary') => {
    const badges = type === 'leave' ? leaveStatusBadges : salaryAdvanceStatusBadges;
    const badge = badges.find(b => b.key === status);
    return badge || { key: status, label: status, variant: 'outline' as const };
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending_ops_initial':
        return <Clock className="h-4 w-4" />;
      case 'forwarded_to_hr':
        return <RefreshCw className="h-4 w-4" />;
      case 'hr_approved':
      case 'ops_final_approved':
      case 'disbursed':
        return <CheckCircle className="h-4 w-4" />;
      case 'hr_rejected':
      case 'ops_final_rejected':
        return <XCircle className="h-4 w-4" />;
      default:
        return <AlertTriangle className="h-4 w-4" />;
    }
  };

  return (
    <div className={`space-y-6 ${className || ''}`}>
      {error && (
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertTitle>Error</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <User className="h-5 w-5" />
            Workflow Dashboard
          </CardTitle>
          <CardDescription>
            Manage leave requests and salary advances workflow
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Tabs value={activeTab} onValueChange={setActiveTab}>
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="leave">Leave Requests</TabsTrigger>
              <TabsTrigger value="salary">Salary Advances</TabsTrigger>
            </TabsList>
            <TabsContent value="leave" className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Leave Requests</h3>
                <Button 
                  onClick={() => loadData(true)} 
                  disabled={loading}
                  variant="outline"
                  size="sm"
                >
                  {loading ? <RefreshCw className="h-4 w-4 animate-spin" /> : <RefreshCw className="h-4 w-4" />}
                  Refresh
                </Button>
              </div>
              
              {leaveRequests.length === 0 ? (
                <Alert>
                  <AlertTriangle className="h-4 w-4" />
                  <AlertTitle>No Leave Requests</AlertTitle>
                  <AlertDescription>
                    There are no leave requests to display.
                  </AlertDescription>
                </Alert>
              ) : (
                <div className="space-y-4">
                  {leaveRequests.map((request) => {
                    const statusBadge = getStatusBadge(request.status, 'leave');
                    return (
                      <Card key={request.id}>
                        <CardContent className="pt-6">
                          <div className="flex items-center justify-between">
                            <div className="space-y-2">
                              <div className="flex items-center gap-2">
                                {getStatusIcon(request.status)}
                                <span className="font-medium">{request.employeeName}</span>
                                <Badge 
                                  variant={statusBadge.variant}
                                  key={statusBadge.key}
                                >
                                  {statusBadge.label}
                                </Badge>
                              </div>
                              <p className="text-sm text-muted-foreground">
                                {request.leaveType}  {request.days} days  {request.startDate} to {request.endDate}
                              </p>
                              <p className="text-sm">{request.reason}</p>
                            </div>
                            
                            <div className="flex gap-2">
                              {request.status === 'pending_ops_initial' && user?.role === 'OPERATIONS_MANAGER' && (
                                <Button
                                  size="sm"
                                  onClick={() => handleLeaveAction(request.id, 'forward_to_hr')}
                                  disabled={loading}
                                >
                                  Forward to HR
                                </Button>
                              )}
                              
                              {request.status === 'forwarded_to_hr' && user?.role === 'HR_MANAGER' && (
                                <>
                                  <Button
                                    size="sm"
                                    onClick={() => handleLeaveAction(request.id, 'hr_approve')}
                                    disabled={loading}
                                  >
                                    Approve
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="destructive"
                                    onClick={() => handleLeaveAction(request.id, 'hr_reject')}
                                    disabled={loading}
                                  >
                                    Reject
                                  </Button>
                                </>
                              )}
                              
                              {request.status === 'hr_approved' && user?.role === 'OPERATIONS_MANAGER' && (
                                <>
                                  <Button
                                    size="sm"
                                    onClick={() => handleLeaveAction(request.id, 'ops_approve')}
                                    disabled={loading}
                                  >
                                    Final Approve
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="destructive"
                                    onClick={() => handleLeaveAction(request.id, 'ops_reject')}
                                    disabled={loading}
                                  >
                                    Final Reject
                                  </Button>
                                </>
                              )}
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              )}
            </TabsContent>
            <TabsContent value="salary" className="space-y-4">
              <div className="flex items-center justify-between">
                <h3 className="text-lg font-semibold">Salary Advances</h3>
                <Button 
                  onClick={() => loadData(true)} 
                  disabled={loading}
                  variant="outline"
                  size="sm"
                >
                  {loading ? <RefreshCw className="h-4 w-4 animate-spin" /> : <RefreshCw className="h-4 w-4" />}
                  Refresh
                </Button>
              </div>
              
              {salaryAdvances.length === 0 ? (
                <Alert>
                  <AlertTriangle className="h-4 w-4" />
                  <AlertTitle>No Salary Advances</AlertTitle>
                  <AlertDescription>
                    There are no salary advance requests to display.
                  </AlertDescription>
                </Alert>
              ) : (
                <div className="space-y-4">
                  {salaryAdvances.map((request) => {
                    const statusBadge = getStatusBadge(request.status, 'salary');
                    return (
                      <Card key={request.id}>
                        <CardContent className="pt-6">
                          <div className="flex items-center justify-between">
                            <div className="space-y-2">
                              <div className="flex items-center gap-2">
                                {getStatusIcon(request.status)}
                                <span className="font-medium">{request.employeeName}</span>
                                <Badge 
                                  variant={statusBadge.variant}
                                  key={statusBadge.key}
                                >
                                  {statusBadge.label}
                                </Badge>
                              </div>
                              <p className="text-sm text-muted-foreground">
                                Amount: KES {request.amount.toLocaleString()}  Requested: {request.requestDate}
                              </p>
                              <p className="text-sm">{request.reason}</p>
                            </div>
                            
                            <div className="flex gap-2">
                              {request.status === 'pending_ops_initial' && user?.role === 'OPERATIONS_MANAGER' && (
                                <Button
                                  size="sm"
                                  onClick={() => handleSalaryAdvanceAction(request.id, 'forward_to_hr')}
                                  disabled={loading}
                                >
                                  Forward to HR
                                </Button>
                              )}
                              
                              {request.status === 'forwarded_to_hr' && user?.role === 'HR_MANAGER' && (
                                <>
                                  <Button
                                    size="sm"
                                    onClick={() => handleSalaryAdvanceAction(request.id, 'hr_approve')}
                                    disabled={loading}
                                  >
                                    Approve
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="destructive"
                                    onClick={() => handleSalaryAdvanceAction(request.id, 'hr_reject')}
                                    disabled={loading}
                                  >
                                    Reject
                                  </Button>
                                </>
                              )}
                              
                              {request.status === 'hr_approved' && user?.role === 'OPERATIONS_MANAGER' && (
                                <>
                                  <Button
                                    size="sm"
                                    onClick={() => handleSalaryAdvanceAction(request.id, 'ops_approve')}
                                    disabled={loading}
                                  >
                                    Final Approve
                                  </Button>
                                  <Button
                                    size="sm"
                                    variant="destructive"
                                    onClick={() => handleSalaryAdvanceAction(request.id, 'ops_reject')}
                                    disabled={loading}
                                  >
                                    Final Reject
                                  </Button>
                                </>
                              )}
                              
                              {request.status === 'ops_final_approved' && user?.role === 'ADMIN' && (
                                <Button
                                  size="sm"
                                  onClick={() => handleSalaryAdvanceAction(request.id, 'disburse')}
                                  disabled={loading}
                                >
                                  Disburse
                                </Button>
                              )}
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    );
                  })}
                </div>
              )}
            </TabsContent>
          </Tabs>
        </CardContent>
      </Card>
    </div>
  );
};

export default WorkflowDashboard;

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Separator } from '@/components/ui/separator';
import { CheckCircle, Clock, AlertTriangle, XCircle, ArrowRight, DollarSign } from 'lucide-react';
import { api, SalaryAdvanceRequest } from '@/services/unifiedApi'; // Import SalaryAdvanceRequest directly from unifiedApi
import { LeaveRequest, AdvancePayrollIntegration } from '@/types/types'; // Import other types from types.ts
import { useAuth } from '@/contexts/AuthContext';

const WorkflowTestDashboard = () => {
  const { user } = useAuth();
  const [leaveRequests, setLeaveRequests] = useState<LeaveRequest[]>([]);
  const [salaryAdvances, setSalaryAdvances] = useState<SalaryAdvanceRequest[]>([]);
  const [activeDeductions, setActiveDeductions] = useState<AdvancePayrollIntegration[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadData();
  }, [user]);

  const loadData = async () => {
    if (!user) return;
    
    setLoading(true);
    try { // Load data based on user role
      const role = user.role === 'OPERATIONS_MANAGER' ? 'operations' :
                   user.role === 'HR_MANAGER' ? 'hr' : 'employee';
      
      const [leaves, advances, deductions] = await Promise.all([
        api.data.getLeaveRequests(role, { employeeId: user.id }),
        api.data.getSalaryAdvanceRequests(role, { employeeId: user.id }),
        api.data.getActiveAdvanceDeductions(user.id)
      ]);

      setLeaveRequests(leaves);
      setSalaryAdvances(advances);
      setActiveDeductions(deductions);
    } catch (error) {
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending_ops_initial':
        return <Clock className="h-4 w-4 text-yellow-500" />;
      case 'forwarded_to_hr':
        return <ArrowRight className="h-4 w-4 text-blue-500" />;
      case 'hr_approved':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'hr_rejected':
        return <XCircle className="h-4 w-4 text-red-500" />;
      case 'ops_final_approved':
        return <CheckCircle className="h-4 w-4 text-green-600" />;
      case 'ops_final_rejected':
        return <XCircle className="h-4 w-4 text-red-600" />;
      case 'disbursed':
        return <DollarSign className="h-4 w-4 text-green-500" />;
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-gray-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-400" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending_ops_initial':
        return 'bg-yellow-100 text-yellow-800';
      case 'forwarded_to_hr':
        return 'bg-blue-100 text-blue-800';
      case 'hr_approved':
        return 'bg-green-100 text-green-800';
      case 'hr_rejected':
        return 'bg-red-100 text-red-800';
      case 'ops_final_approved':
        return 'bg-green-100 text-green-900';
      case 'ops_final_rejected':
        return 'bg-red-100 text-red-900';
      case 'disbursed':
        return 'bg-emerald-100 text-emerald-800';
      case 'completed':
        return 'bg-gray-100 text-gray-800';
      default:
        return 'bg-gray-100 text-gray-600';
    }
  };

  const handleLeaveAction = async (requestId: string, action: string) => {
    if (!user) return;
    
    setLoading(true);
    try {
      switch (action) {
        case 'forward_to_hr':
          await api.data.forwardLeaveRequestToHR(requestId, user.id, 'Forwarding for eligibility review');
          break;
        case 'hr_approve':
          await api.data.reviewLeaveRequestHR(requestId, user.id, 'eligible', 'Employee is eligible for leave');
          break;
        case 'hr_reject':
          await api.data.reviewLeaveRequestHR(requestId, user.id, 'not_eligible', 'Employee not eligible for leave');
          break;
        case 'ops_approve':
          await api.data.finalDecisionLeaveRequest(requestId, user.id, 'approved', 'Final approval granted');
          break;
        case 'ops_reject':
          await api.data.finalDecisionLeaveRequest(requestId, user.id, 'rejected', 'Final approval denied');
          break;
      }
      await loadData();
    } catch (error) {
      console.error('Error processing leave action:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSalaryAdvanceAction = async (requestId: number | string, action: string) => {
    if (!user) return;
    
    setLoading(true);
    try {
      switch (action) {
        case 'forward_to_hr':
          await api.data.forwardSalaryAdvanceToHR(requestId, user.id, 'Forwarding for eligibility check');
          break;
        case 'hr_approve':
          await api.data.reviewSalaryAdvanceHR(requestId, user.id, 'eligible', 'Employee eligible for advance', {
            currentSalary: 30000,
            existingAdvances: 0,
            maxAllowableAdvance: 15000,
            employmentTenure: 12,
            creditworthiness: 'good'
          });
          break;
        case 'hr_reject':
          await api.data.reviewSalaryAdvanceHR(requestId, user.id, 'not_eligible', 'Employee not eligible for advance');
          break;
        case 'ops_approve':
          await api.data.finalDecisionSalaryAdvance(requestId, user.id, 'approved', 'Final approval granted');
          break;
        case 'ops_reject':
          await api.data.finalDecisionSalaryAdvance(requestId, user.id, 'rejected', 'Final approval denied');
          break;
        case 'disburse':
          await api.data.disburseSalaryAdvance(requestId, user.id);
          break;
      }
      await loadData();
    } catch (error) {
      console.error('Error processing salary advance action:', error);
    } finally {
      setLoading(false);
    }
  };

  const simulatePayrollDeduction = async (salaryAdvanceId: number | string) => {
    setLoading(true);
    try {
      await api.data.processAdvanceDeduction(salaryAdvanceId, 'PP-2024-12', 2500);
      await loadData();
    } catch (error) {
      console.error('Error processing payroll deduction:', error);
    } finally {
      setLoading(false);
    }
  };

  const getAvailableActions = (request: LeaveRequest | SalaryAdvanceRequest, type: 'leave' | 'advance') => {
    const actions = [];
    
    if (user?.role === 'OPERATIONS_MANAGER') {
      if (request.status === 'pending_ops_initial') {
        actions.push({ key: 'forward_to_hr', label: 'Forward to HR', variant: 'default' as const });
      }
      if (request.status === 'hr_approved') {
        actions.push({ key: 'ops_approve', label: 'Approve', variant: 'default' as const });
        actions.push({ key: 'ops_reject', label: 'Reject', variant: 'destructive' as const });
      }
      if (request.status === 'hr_rejected') {
        actions.push({ key: 'ops_reject', label: 'Reject', variant: 'destructive' as const });
      }
      if (type === 'advance' && request.status === 'ops_final_approved') {
        actions.push({ key: 'disburse', label: 'Disburse', variant: 'default' as const });
      }
    }
    
    if (user?.role === 'HR_MANAGER') {
      if (request.status === 'forwarded_to_hr') {
        actions.push({ key: 'hr_approve', label: 'Approve Eligibility', variant: 'default' as const });
        actions.push({ key: 'hr_reject', label: 'Reject Eligibility', variant: 'destructive' as const });
      }
    }
    
    return actions;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <Clock className="h-8 w-8 animate-spin mx-auto mb-2" />
          <p>Loading workflow data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Workflow Test Dashboard</h2>
          <p className="text-muted-foreground">
            Test interconnectivity between leave requests, salary advances, and payroll integration
          </p>
        </div>
        <Button onClick={loadData} disabled={loading}>
          Refresh Data
        </Button>
      </div>

      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertTitle>Workflow Testing Environment</AlertTitle>
        <AlertDescription>
          This dashboard demonstrates the corrected workflow where Operations Managers forward requests to HR for eligibility checks, 
          then make final decisions based on HR feedback. Salary advances automatically integrate with payroll for deductions.
        </AlertDescription>
      </Alert>

      <Tabs defaultValue="leave" className="space-y-4">
        <TabsList>
          <TabsTrigger value="leave">Leave Requests ({leaveRequests.length})</TabsTrigger>
          <TabsTrigger value="advances">Salary Advances ({salaryAdvances.length})</TabsTrigger>
          <TabsTrigger value="payroll">Payroll Integration ({activeDeductions.length})</TabsTrigger>
        </TabsList>

        <TabsContent value="leave" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Leave Request Workflow</CardTitle>
              <CardDescription>
                Employee  Operations Manager  HR (Eligibility)  Operations Manager (Final Decision)
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {leaveRequests.length === 0 ? (
                <p className="text-center text-muted-foreground py-8">No leave requests found</p>
              ) : (
                leaveRequests.map((request) => (
                  <div key={request.id} className="border rounded-lg p-4 space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(request.status)}
                        <span className="font-medium">{request.employeeName}</span>
                        <Badge className={getStatusColor(request.status)}>
                          {request.status.replace(/_/g, ' ').toUpperCase()}
                        </Badge>
                      </div>
                      <span className="text-sm text-muted-foreground">
                        {request.days} days  {request.leaveType}
                      </span>
                    </div>
                    
                    <div className="text-sm text-muted-foreground">
                      <p><strong>Dates:</strong> {request.startDate} to {request.endDate}</p>
                      <p><strong>Reason:</strong> {request.reason}</p>
                      <p><strong>Current Step:</strong> {request.currentStep.replace(/_/g, ' ')}</p>
                    </div>

                    {request.workflowHistory.length > 0 && (
                      <div className="space-y-2">
                        <Separator />
                        <h4 className="text-sm font-medium">Workflow History:</h4>
                        {request.workflowHistory.map((step, index) => (
                          <div key={step.id} className="text-xs bg-muted p-2 rounded">
                            <span className="font-medium">{step.actorName}</span> {step.action} 
                            <span className="text-muted-foreground ml-1">
                              ({new Date(step.timestamp).toLocaleString()})
                            </span>
                            {step.comments && <p className="mt-1 italic">"{step.comments}"</p>}
                          </div>
                        ))}
                      </div>
                    )}

                    <div className="flex gap-2">
                      {getAvailableActions(request, 'leave').map((action) => (
                        <Button
                          key={action.key}
                          size="sm"
                          variant={action.variant}
                          onClick={() => handleLeaveAction(request.id, action.key)}
                          disabled={loading}
                        >
                          {action.label}
                        </Button>
                      ))}
                    </div>
                  </div>
                ))
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="advances" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Salary Advance Workflow</CardTitle>
              <CardDescription>
                Employee  Operations Manager  HR (Eligibility)  Operations Manager (Final Decision)  Disbursement  Payroll Integration
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {salaryAdvances.length === 0 ? (
                <p className="text-center text-muted-foreground py-8">No salary advance requests found</p>
              ) : (
                salaryAdvances.map((request) => (
                  <div key={request.id} className="border rounded-lg p-4 space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        {getStatusIcon(request.status)}
                        <span className="font-medium">{request.employeeName}</span>
                        <Badge className={getStatusColor(request.status)}>
                          {request.status.replace(/_/g, ' ').toUpperCase()}
                        </Badge>
                      </div>
                      <span className="text-sm text-muted-foreground">
                        KSH {request.amount.toLocaleString()}
                      </span>
                    </div>
                    
                    <div className="text-sm text-muted-foreground">
                      <p><strong>Reason:</strong> {request.reason}</p>
                      <p><strong>Current Step:</strong> {request.currentStep.replace(/_/g, ' ')}</p>
                      <p><strong>Repayment:</strong> KSH {request.payrollIntegration.monthlyDeduction.toLocaleString()}/month for {request.payrollIntegration.repaymentMonths} months</p>
                      {request.repaymentDetails.remainingBalance > 0 && (
                        <p><strong>Remaining Balance:</strong> KSH {request.repaymentDetails.remainingBalance.toLocaleString()}</p>
                      )}
                    </div>

                    {request.hrEligibilityDetails && (
                      <div className="bg-blue-50 p-3 rounded text-sm">
                        <h4 className="font-medium mb-2">HR Eligibility Assessment:</h4>
                        <div className="grid grid-cols-2 gap-2 text-xs">
                          <span>Current Salary: KSH {request.hrEligibilityDetails.currentSalary.toLocaleString()}</span>
                          <span>Max Allowable: KSH {request.hrEligibilityDetails.maxAllowableAdvance.toLocaleString()}</span>
                          <span>Employment Tenure: {request.hrEligibilityDetails.employmentTenure} months</span>
                          <span>Creditworthiness: {request.hrEligibilityDetails.creditworthiness}</span>
                        </div>
                      </div>
                    )}

                    {request.workflowHistory.length > 0 && (
                      <div className="space-y-2">
                        <Separator />
                        <h4 className="text-sm font-medium">Workflow History:</h4>
                        {request.workflowHistory.map((step) => (
                          <div key={step.id} className="text-xs bg-muted p-2 rounded">
                            <span className="font-medium">{step.actorName}</span> {step.action} 
                            <span className="text-muted-foreground ml-1">
                              ({new Date(step.timestamp).toLocaleString()})
                            </span>
                            {step.comments && <p className="mt-1 italic">"{step.comments}"</p>}
                          </div>
                        ))}
                      </div>
                    )}

                    <div className="flex gap-2">
                      {getAvailableActions(request, 'advance').map((action) => (
                        <Button
                          key={action.key}
                          size="sm"
                          variant={action.variant}
                          onClick={() => handleSalaryAdvanceAction(request.id, action.key)}
                          disabled={loading}
                        >
                          {action.label}
                        </Button>
                      ))}
                    </div>
                  </div>
                ))
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="payroll" className="space-y-4">
          <Card>
            <CardHeader>
              <CardTitle>Payroll Integration</CardTitle>
              <CardDescription>
                Active salary advance deductions that will be processed during payroll
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              {activeDeductions.length === 0 ? (
                <p className="text-center text-muted-foreground py-8">No active payroll deductions found</p>
              ) : (
                activeDeductions.map((deduction) => (
                  <div key={`${deduction.salaryAdvanceId}-${deduction.employeeId}`} className="border rounded-lg p-4 space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center gap-2">
                        <DollarSign className="h-4 w-4 text-green-500" />
                        <span className="font-medium">Advance #{deduction.salaryAdvanceId}</span>
                        <Badge className="bg-green-100 text-green-800">ACTIVE</Badge>
                      </div>
                      <span className="text-sm text-muted-foreground">
                        Employee ID: {deduction.employeeId}
                      </span>
                    </div>
                    
                    <div className="grid grid-cols-2 gap-4 text-sm">
                      <div>
                        <p><strong>Monthly Deduction:</strong> KSH {deduction.monthlyDeductionAmount.toLocaleString()}</p>
                        <p><strong>Remaining Balance:</strong> KSH {deduction.remainingBalance.toLocaleString()}</p>
                      </div>
                      <div>
                        <p><strong>Start Date:</strong> {deduction.startDate}</p>
                        <p><strong>Priority:</strong> {deduction.priority}</p>
                      </div>
                    </div>

                    <div className="flex gap-2">
                      <Button
                        size="sm"
                        onClick={() => simulatePayrollDeduction(deduction.salaryAdvanceId)}
                        disabled={loading}
                      >
                        Simulate Payroll Deduction
                      </Button>
                    </div>
                  </div>
                ))
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
};

