import { Checkbox } from '@/components/ui/checkbox';
import { useToast } from '@/hooks/use-toast';
import { Users, UserPlus, Edit, Trash2, Shield, Search, Settings, Key, UserCheck, UserX, Loader2, Eye, EyeOff } from 'lucide-react';
import { userService, CreateUserRequest } from '@/services/api/user.service';
import { branchService } from '@/services/api/branch.service';
import { employeeService } from '@/services/api/employee.service';
import { User, Branch } from '@/types/types';

const initialAddUser = {
  firstName: '',
  lastName: '',
  email: '',
  password: '',
  role: '',
};

const UserManagement: React.FC = () => {
  const { toast } = useToast();
  const [searchTerm, setSearchTerm] = useState('');
  const [filterRole, setFilterRole] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  // Department filter removed
  const [activeTab, setActiveTab] = useState('users');
  const [users, setUsers] = useState<User[]>([]);
  const [roles, setRoles] = useState<Array<{ id: string; name: string; description: string; permissions: string[] }>>([]);
  // const [departments, setDepartments] = useState<Array<{ id: string; name: string; description: string }>>([]);
  // const [branches, setBranches] = useState<Branch[]>([]);
  const [permissions, setPermissions] = useState<string[]>([]);
  const [userStats, setUserStats] = useState({ totalUsers: 0, activeUsers: 0, adminUsers: 0, hrManagerUsers: 0 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [editFields, setEditFields] = useState<{ [userId: string]: any }>({});
  const [passwordFields, setPasswordFields] = useState<{ [userId: string]: { newPassword: string; confirmPassword: string } }>({});
  const [passwordVisibility, setPasswordVisibility] = useState<{ [userId: string]: { new: boolean; confirm: boolean } }>({});
  const [addUserDialogOpen, setAddUserDialogOpen] = useState(false);
  const [addUser, setAddUser] = useState(initialAddUser);
  const [addUserLoading, setAddUserLoading] = useState(false);
  const [addUserErrors, setAddUserErrors] = useState<{ [k: string]: string }>({});

  // Load data on component mount
  useEffect(() => {
    loadData();
  }, []);

  const loadData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      // Load all data in parallel
      const [usersResponse, rolesResponse, branchesResponse, permissionsResponse, statsResponse] = await Promise.all([
        userService.getUsers(),
        userService.getRoles(),
        branchService.getAllBranches(),
        userService.getPermissions(),
        userService.getUserStats()
      ]);

      let users: User[] = Array.isArray(usersResponse.data) ? usersResponse.data : [];

      // No branch merging needed
      setUsers(users);
      setRoles(Array.isArray(rolesResponse) ? rolesResponse : []);
      // setDepartments(Array.isArray(departmentsResponse) ? departmentsResponse : []);
      // setDepartments(Array.isArray(departmentsResponse) ? departmentsResponse : []);
      // Departments loading removed due to missing API
      setPermissions(Array.isArray(permissionsResponse) ? permissionsResponse : []);
      setUserStats(statsResponse || { totalUsers: 0, activeUsers: 0, adminUsers: 0, hrManagerUsers: 0 });

    } catch (error) {
      console.error('Failed to load data:', error);
      setError('Failed to load user data. Please try again.');
      toast({
        title: "Error",
        description: "Failed to load user data. Please try again.",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  }, [toast]);

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case 'ADMIN':
        return 'bg-red-100 text-red-800';
      case 'HR_MANAGER':
        return 'bg-blue-100 text-blue-800';
      case 'OPERATIONS_MANAGER':
        return 'bg-purple-100 text-purple-800';
      case 'EMPLOYEE':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusBadgeColor = (status: string) => {
    switch (status?.toUpperCase()) {
      case 'ACTIVE':
        return 'bg-green-100 text-green-800';
      case 'INACTIVE':
        return 'bg-gray-100 text-gray-800';
      case 'SUSPENDED':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  // Helper to safely get string value
  const safeToString = (val: any) => (val !== undefined && val !== null ? val.toString() : '');

  // Helper to get department/branch name by ID
  // const getDepartmentName = (id: string | null) => {
  //   if (!id) return 'None';
  //   const dept = departments.find(d => d.id === id);
  //   return dept ? dept.name : 'None';
  // };
  // const getBranchName = (id: string | null) => {
  //   if (!id) return 'None';
  //   const branch = branches.find(b => b.id === id);
  //   return branch ? branch.name : 'None';
  // };

  // Defensive filter: only include valid user objects
  const filteredUsers = useMemo(() => users.filter(user => {
    if (!user || typeof user !== 'object') return false;
    // Defensive: check for required fields
    if (!('status' in user) || !('role' in user) || !('firstName' in user)) return false;
    const matchesSearch = (user.firstName?.toLowerCase() || '').includes(searchTerm.toLowerCase()) ||
      (user.lastName?.toLowerCase() || '').includes(searchTerm.toLowerCase()) ||
      (user.email?.toLowerCase() || '').includes(searchTerm.toLowerCase());
    const matchesRole = filterRole === 'all' || user.role === filterRole || safeToString(user.role) === filterRole;
    const matchesStatus = filterStatus === 'all' || (user.status?.toUpperCase() === filterStatus.toUpperCase());
    return matchesSearch && matchesRole && matchesStatus;
  }), [users, searchTerm, filterRole, filterStatus]);

  const handleDeleteUser = async (userId: string) => {
    try {
      await userService.deleteUser(userId);
      setUsers(prev => prev.filter(u => u.id !== userId));
      toast({
        title: "User Deleted",
        description: "User has been successfully removed from the system.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to delete user.",
        variant: "destructive",
      });
    }
  };

  const handleToggleUserStatus = async (userId: string, currentStatus: string) => {
    const newStatus = currentStatus?.toUpperCase() === 'ACTIVE' ? 'INACTIVE' : 'ACTIVE';
    try {
      const updatedUser = await userService.updateUserStatus(userId, newStatus);
      if (updatedUser && typeof updatedUser.status !== 'undefined') {
        setUsers(prev => prev.map(u => u.id === userId ? { ...u, status: updatedUser.status } : u));
        toast({
          title: "User Status Updated",
          description: `User status changed to ${newStatus}.`,
        });
      } else {
        toast({
          title: "Error",
          description: "Failed to update user status. Invalid response from server.",
          variant: "destructive",
        });
      }
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update user status.",
        variant: "destructive",
      });
    }
  };

  const handleUpdateUser = async (userId: string, userData: any) => {
    // Only send allowed fields
    const allowedFields = [
      'firstName', 'lastName', 'email', 'role', 'status', 'department', 'permissions'
    ];
    const sanitizedData: any = {};
    allowedFields.forEach(field => {
      if (userData && userData[field] !== undefined) {
        sanitizedData[field] = userData[field];
      }
    });
    console.log('Updating user:', userId, sanitizedData); // DEBUG
    try {
      const updatedUser = await userService.updateUser(userId, sanitizedData);
      setUsers(prev => prev.map(u => u.id === userId && updatedUser ? updatedUser : u));
      toast({
        title: "User Updated",
        description: "User details have been successfully updated.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update user.",
        variant: "destructive",
      });
    }
  };

  const handleResetPassword = async (userId: string, newPassword: string) => {
    try {
      await userService.changeUserPassword({ userId, newPassword });
      toast({
        title: "Password Reset",
        description: "User password has been successfully reset.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to reset password.",
        variant: "destructive",
      });
    }
  };

  const handleUpdatePermissions = async (userId: string, permissions: string[]) => {
    try {
      const updatedUser = await userService.updateUserPermissions(userId, permissions);
      setUsers(prev => prev.map(u => u.id === userId ? updatedUser : u));
      toast({
        title: "Permissions Updated",
        description: "User permissions have been successfully updated.",
      });
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update permissions.",
        variant: "destructive",
      });
    }
  };

  const availablePermissions = [
    'user_management',
    'system_admin',
    'reports',
    'workflow_management',
    'salary_advances',
    'leave_management',
    'employee_management',
    'performance_management',
    'branch_management',
    'leave_approval',
    'salary_advance_approval',
    'staff_scheduling',
    'profile_view',
    'leave_request',
    'salary_advance_request',
    'staff_coordination'
  ];

  const getPermissionsByRole = (role: string): string[] => {
    switch (role) {
      case 'admin':
        return ['user_management', 'system_admin', 'reports', 'workflow_management', 'salary_advances', 'leave_management'];
      case 'hr_manager':
        return ['employee_management', 'leave_management', 'salary_advances', 'reports', 'performance_management'];
      case 'operations_manager':
        return ['branch_management', 'leave_approval', 'salary_advance_approval', 'staff_scheduling', 'reports'];
      case 'employee':
        return ['profile_view', 'leave_request', 'salary_advance_request'];
      default:
        return ['profile_view'];
    }
  };

  // Add User logic
  const validateAddUser = (data: typeof initialAddUser) => {
    const errors: { [k: string]: string } = {};
    if (!data.firstName.trim()) errors.firstName = 'First name is required';
    if (!data.lastName.trim()) errors.lastName = 'Last name is required';
    if (!data.email.trim()) errors.email = 'Email is required';
    if (!data.password.trim() || data.password.length < 6) errors.password = 'Password (min 6 chars) is required';
    if (!data.role) errors.role = 'Role is required';
    return errors;
  };

  const handleAddUser = async () => {
    const errors = validateAddUser(addUser);
    setAddUserErrors(errors);
    if (Object.keys(errors).length > 0) return;
    setAddUserLoading(true);
    try {
      // Only create employee, backend will auto-create user
      // Match the expected type for employeeService.createEmployee
      await employeeService.createEmployee({
        firstName: addUser.firstName.trim(),
        lastName: addUser.lastName.trim(),
        email: addUser.email.trim(),
        position: addUser.role, // or set to a default/empty string if needed
        hireDate: new Date().toISOString(),
        phone: '',
        department: '', // Provide a default or select value if available
        branch: '',     // Provide a default or select value if available
      });
      // Reload users to ensure correct department/branch display
      await loadData();
      setAddUser(initialAddUser);
      setAddUserDialogOpen(false);
      setAddUserErrors({});
      toast({ title: 'User Added', description: `${addUser.firstName} ${addUser.lastName} has been added.` });
    } catch (error: any) {
      toast({ title: 'Error', description: error.message || 'Failed to add user.', variant: 'destructive' });
    } finally {
      setAddUserLoading(false);
    }
  };

  // Helper type guard for objects with a name property
  function hasNameProp(obj: any): obj is { name: string } {
    return obj && typeof obj === 'object' && 'name' in obj;
  }

  return (
    <DashboardLayout title="User Management">
      <div className="space-y-6">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Users</CardTitle>
              <Users className="h-4 w-4 text-blue-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{userStats.totalUsers ?? users.length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Users</CardTitle>
              <Users className="h-4 w-4 text-green-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{userStats.activeUsers ?? users.filter(u => u.status === 'active').length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Administrators</CardTitle>
              <Shield className="h-4 w-4 text-red-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{userStats.adminUsers ?? users.filter(u => u.role === 'ADMIN').length}</div>
            </CardContent>
          </Card>
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">HR Managers</CardTitle>
              <Users className="h-4 w-4 text-yellow-600" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{userStats.hrManagerUsers ?? users.filter(u => u.role === 'HR_MANAGER').length}</div>
            </CardContent>
          </Card>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="space-y-4">
          <TabsList>
            <TabsTrigger value="users">Users</TabsTrigger>
            <TabsTrigger value="roles">Roles & Permissions</TabsTrigger>
          </TabsList>

          <TabsContent value="users" className="space-y-4">
            <Card>
              <CardHeader>
                <div className="flex justify-between items-center">
                  <div>
                    <CardTitle>User Management</CardTitle>
                    <CardDescription>Manage system users and their access</CardDescription>
                  </div>
                  <Dialog open={addUserDialogOpen} onOpenChange={setAddUserDialogOpen}>
                    <DialogTrigger asChild>
                      <Button className="bg-green-600 hover:bg-green-700 text-white">
                        <UserPlus className="mr-2 h-4 w-4 text-white" />
                        Add User
                      </Button>
                    </DialogTrigger>
                    <DialogContent>
                      <DialogHeader>
                        <DialogTitle>Add New User</DialogTitle>
                        <DialogDescription>Create a new user account</DialogDescription>
                      </DialogHeader>
                      <div className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <Label htmlFor="firstName">First Name</Label>
                            <Input id="firstName" value={addUser.firstName} onChange={e => setAddUser(a => ({ ...a, firstName: e.target.value }))} placeholder="Enter first name" />
                            {addUserErrors.firstName && <div className="text-xs text-red-500">{addUserErrors.firstName}</div>}
                          </div>
                          <div>
                            <Label htmlFor="lastName">Last Name</Label>
                            <Input id="lastName" value={addUser.lastName} onChange={e => setAddUser(a => ({ ...a, lastName: e.target.value }))} placeholder="Enter last name" />
                            {addUserErrors.lastName && <div className="text-xs text-red-500">{addUserErrors.lastName}</div>}
                          </div>
                        </div>
                        <div>
                          <Label htmlFor="email">Email</Label>
                          <Input id="email" type="email" value={addUser.email} onChange={e => setAddUser(a => ({ ...a, email: e.target.value }))} placeholder="Enter email address" />
                          {addUserErrors.email && <div className="text-xs text-red-500">{addUserErrors.email}</div>}
                        </div>
                        <div>
                          <Label htmlFor="password">Password</Label>
                          <Input id="password" type="password" value={addUser.password} onChange={e => setAddUser(a => ({ ...a, password: e.target.value }))} placeholder="Set initial password" />
                          {addUserErrors.password && <div className="text-xs text-red-500">{addUserErrors.password}</div>}
                        </div>
                        <div>
                          <Label htmlFor="role">Role</Label>
                          <Select value={addUser.role} onValueChange={val => setAddUser(a => ({ ...a, role: val }))}>
                            <SelectTrigger>
                              <SelectValue placeholder="Select role" />
                            </SelectTrigger>
                            <SelectContent>
                              {roles.map(role => (
                                <SelectItem key={role.id} value={role.id}>{role.name}</SelectItem>
                              ))}
                            </SelectContent>
                          </Select>
                          {addUserErrors.role && <div className="text-xs text-red-500">{addUserErrors.role}</div>}
                        </div>
                        {/* Department and Branch fields removed */}
                        <Button className="w-full" onClick={handleAddUser} disabled={addUserLoading}>
                          {addUserLoading ? <Loader2 className="animate-spin h-4 w-4 mr-2" /> : null} Create User
                        </Button>
                      </div>
                    </DialogContent>
                  </Dialog>
                </div>
              </CardHeader>
              <CardContent>
                {/* Search and Filter */}
                <div className="flex gap-4 mb-6">
                  <div className="flex-1">
                    <div className="relative">
                      <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
                      <Input
                        placeholder="Search users..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="pl-9"
                      />
                    </div>
                  </div>
                  <Select value={filterRole} onValueChange={setFilterRole}>
                    <SelectTrigger className="w-48">
                      <SelectValue placeholder="Filter by role" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Roles</SelectItem>
                      {roles
                        .filter(role => role && role.id && safeToString(role.id).trim() && role.name && role.name.trim())
                        .map(role => (
                          <SelectItem key={safeToString(role.id)} value={safeToString(role.id)}>
                            {role.name}
                          </SelectItem>
                        ))}
                    </SelectContent>
                  </Select>
                  {/* Department filter removed due to missing departments API */}
                  <Select value={filterStatus} onValueChange={setFilterStatus}>
                    <SelectTrigger className="w-48">
                      <SelectValue placeholder="Filter by status" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Status</SelectItem>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="inactive">Inactive</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {/* Users Table */}
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>User</TableHead>
                      <TableHead>Email</TableHead>
                      <TableHead>Role</TableHead>
                      {/* Department and Branch columns removed */}
                      <TableHead>Status</TableHead>
                      <TableHead>Last Login</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredUsers.map((user) => (
                      user ? (
                        <TableRow key={user?.id || Math.random()}>
                          <TableCell>
                            <div>
                              <div className="font-medium">{user?.firstName || ''} {user?.lastName || ''}</div>
                              <div className="text-sm text-muted-foreground">{user?.employeeId || ''}</div>
                            </div>
                          </TableCell>
                          <TableCell>{user?.email || ''}</TableCell>
                          <TableCell>
                            <Badge className={getRoleBadgeColor(user?.role || '')}>
                              {(user?.role || '').replace('_', ' ')}
                            </Badge>
                          </TableCell>
                          {/* Department and Branch cells removed */}
                          <TableCell>
                          <Badge className={getStatusBadgeColor(user?.status || '')}>
                              {user?.status ? user.status.charAt(0) + user.status.slice(1).toLowerCase() : ''}
                          </Badge>
                          </TableCell>
                          <TableCell className="text-sm">{user.lastLogin}</TableCell>
                          <TableCell>
                            <div className="flex gap-2">
                              {/* Edit User Dialog Trigger */}
                              <Dialog>
                                <DialogTrigger asChild>
                                  <Button variant="outline" size="sm" className="hover:bg-blue-100">
                                    <Edit className="h-4 w-4 text-blue-600" />
                                  </Button>
                                </DialogTrigger>
                                <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                                  <DialogHeader>
                                    <DialogTitle>Edit User: {user.firstName} {user.lastName}</DialogTitle>
                                    <DialogDescription>Update user details, role, permissions, and password.</DialogDescription>
                                  </DialogHeader>
                                  <div className="space-y-6">
                                    {/* Basic User Info */}
                                    <div className="space-y-4">
                                      <h4 className="font-semibold">Basic Information</h4>
                                      <div className="grid grid-cols-2 gap-4">
                                        <div>
                                          <Label htmlFor={`edit-firstName-${user.id}`}>First Name</Label>
                                          <Input id={`edit-firstName-${user.id}`} defaultValue={user.firstName} onChange={e => setEditFields(f => ({ ...f, [user.id]: { ...f[user.id], firstName: e.target.value } }))} />
                                        </div>
                                        <div>
                                          <Label htmlFor={`edit-lastName-${user.id}`}>Last Name</Label>
                                          <Input id={`edit-lastName-${user.id}`} defaultValue={user.lastName} onChange={e => setEditFields(f => ({ ...f, [user.id]: { ...f[user.id], lastName: e.target.value } }))} />
                                        </div>
                                      </div>
                                      <div>
                                        <Label htmlFor={`edit-email-${user.id}`}>Email</Label>
                                        <Input id={`edit-email-${user.id}`} type="email" defaultValue={user.email} onChange={e => setEditFields(f => ({ ...f, [user.id]: { ...f[user.id], email: e.target.value } }))} />
                                      </div>
                                      <div className="grid grid-cols-2 gap-4">
                                        {/* Department and Branch fields removed from edit dialog */}
                                      </div>
                                    </div>

                                    {/* Role & Status Management */}
                                    <div className="space-y-4 border-t pt-4">
                                      <h4 className="font-semibold">Role & Status</h4>
                                      <div className="grid grid-cols-2 gap-4">
                                        <div>
                                          <Label htmlFor={`edit-role-${user.id}`}>Role</Label>
                                          <Select defaultValue={user.role} onValueChange={val => setEditFields(f => ({ ...f, [user.id]: { ...f[user.id], role: val } }))}>
                                            <SelectTrigger id={`edit-role-${user.id}`}>
                                              <SelectValue placeholder="Select role" />
                                            </SelectTrigger>
                                            <SelectContent>
                                              <SelectItem value="ADMIN">Administrator</SelectItem>
                                              <SelectItem value="HR_MANAGER">HR Manager</SelectItem>
                                              <SelectItem value="OPERATIONS_MANAGER">Operations Manager</SelectItem>
                                              <SelectItem value="EMPLOYEE">Employee</SelectItem>
                                            </SelectContent>
                                          </Select>
                                        </div>
                                        <div>
                                          <Label htmlFor={`edit-status-${user.id}`}>Status</Label>
                                          <Select defaultValue={user.status} onValueChange={val => setEditFields(f => ({ ...f, [user.id]: { ...f[user.id], status: val } }))}>
                                            <SelectTrigger id={`edit-status-${user.id}`}>
                                              <SelectValue placeholder="Select status" />
                                            </SelectTrigger>
                                            <SelectContent>
                                              <SelectItem value="active">Active</SelectItem>
                                              <SelectItem value="inactive">Inactive</SelectItem>
                                              <SelectItem value="suspended">Suspended</SelectItem>
                                            </SelectContent>
                                          </Select>
                                        </div>
                                      </div>
                                    </div>

                                    {/* Permissions Management */}
                                    <div className="space-y-4 border-t pt-4">
                                      <div className="flex items-center justify-between">
                                        <h4 className="font-semibold">Permissions</h4>
                                        <Button
                                          variant="outline"
                                          size="sm"
                                          onClick={() => {
                                            const rolePermissions = getPermissionsByRole(user.role);
                                            // TODO: Update permissions checkboxes based on role
                                          }}
                                        >
                                          <Settings className="h-4 w-4 mr-1" />
                                          Set by Role
                                        </Button>
                                      </div>
                                      <div className="grid grid-cols-2 gap-2 max-h-48 overflow-y-auto border rounded p-3">
                                        {availablePermissions.map((permission) => (
                                          <div key={permission} className="flex items-center space-x-2">
                                            <Checkbox
                                              id={`permission-${user.id}-${permission}`}
                                              defaultChecked={user.permissions?.includes(permission)}
                                            />
                                            <Label
                                              htmlFor={`permission-${user.id}-${permission}`}
                                              className="text-sm font-normal cursor-pointer"
                                            >
                                              {permission.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                                            </Label>
                                          </div>
                                        ))}
                                      </div>
                                      <div className="text-xs text-muted-foreground">
                                        Current permissions: {user.permissions?.length || 0} selected
                                      </div>
                                    </div>

                                    {/* Password Reset Section */}
                                    <div className="border-t pt-4 space-y-4">
                                      <h4 className="font-semibold flex items-center">
                                        <Key className="h-4 w-4 mr-2" />
                                        Password Management
                                      </h4>
                                      <div className="space-y-2">
                                        <Label htmlFor={`new-password-${user.id}`}>New Password</Label>
                                        <div className="relative">
                                          <Input
                                            id={`new-password-${user.id}`}
                                            type={passwordVisibility[user.id]?.new ? "text" : "password"}
                                            placeholder="Enter new password"
                                            onChange={e => setPasswordFields(f => ({ ...f, [user.id]: { ...f[user.id], newPassword: e.target.value } }))}
                                            value={passwordFields[user.id]?.newPassword || ""}
                                          />
                                          <Button
                                            type="button"
                                            variant="ghost"
                                            size="icon"
                                            className="absolute right-2 top-2"
                                            onClick={() => setPasswordVisibility(v => ({ ...v, [user.id]: { ...v[user.id], new: !v[user.id]?.new } }))}
                                            tabIndex={-1}
                                          >
                                            {passwordVisibility[user.id]?.new ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                          </Button>
                                        </div>
                                        <Label htmlFor={`confirm-password-${user.id}`}>Confirm Password</Label>
                                        <div className="relative">
                                          <Input
                                            id={`confirm-password-${user.id}`}
                                            type={passwordVisibility[user.id]?.confirm ? "text" : "password"}
                                            placeholder="Confirm new password"
                                            onChange={e => setPasswordFields(f => ({ ...f, [user.id]: { ...f[user.id], confirmPassword: e.target.value } }))}
                                            value={passwordFields[user.id]?.confirmPassword || ""}
                                          />
                                          <Button
                                            type="button"
                                            variant="ghost"
                                            size="icon"
                                            className="absolute right-2 top-2"
                                            onClick={() => setPasswordVisibility(v => ({ ...v, [user.id]: { ...v[user.id], confirm: !v[user.id]?.confirm } }))}
                                            tabIndex={-1}
                                          >
                                            {passwordVisibility[user.id]?.confirm ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                                          </Button>
                                        </div>
                                        <Button variant="secondary" size="sm" className="w-full" onClick={() => handleResetPassword(user.id, passwordFields[user.id]?.newPassword)}>
                                          Reset Password
                                        </Button>
                                      </div>
                                    </div>

                                    {/* Action Buttons */}
                                    <div className="flex gap-2 pt-4">
                                      <Button className="flex-1" onClick={() => handleUpdateUser(user.id, editFields[user.id])}>
                                        Save All Changes
                                      </Button>
                                      <Button
                                        variant="outline"
                                        onClick={() => handleUpdatePermissions(user.id, user.permissions || [])}
                                      >
                                        Update Permissions Only
                                      </Button>
                                    </div>
                                  </div>
                                </DialogContent>
                              </Dialog>

                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleToggleUserStatus(user.id, user.status)} className={user.status === 'active' ? 'hover:bg-yellow-100' : 'hover:bg-green-100'}>
                                {user.status === 'active' ? 'Deactivate' : 'Activate'}
                              </Button>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleDeleteUser(user.id)} className="hover:bg-red-100">
                                <Trash2 className="h-4 w-4 text-red-600" />
                              </Button>
                            </div>
                          </TableCell>
                        </TableRow>
                      ) : null
                    ))}
                  </TableBody>
                </Table>
              </CardContent>
            </Card>
          </TabsContent>


          <TabsContent value="roles" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Roles & Permissions</CardTitle>
                <CardDescription>Manage system roles and their permissions for the nightclub/restaurant HRMS</CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-6">
                  {/* Role Overview Cards */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {[
                      {
                        role: 'ADMIN',
                        name: 'Administrator',
                        description: 'Full system access and user management',
                        userCount: users.filter(u => u.role === 'ADMIN').length,
                        permissions: getPermissionsByRole('admin')
                      },
                      {
                        role: 'HR_MANAGER',
                        name: 'HR Manager',
                        description: 'Employee management and HR operations',
                        userCount: users.filter(u => u.role === 'HR_MANAGER').length,
                        permissions: getPermissionsByRole('hr_manager')
                      },
                      {
                        role: 'OPERATIONS_MANAGER',
                        name: 'Operations Manager',
                        description: 'Branch management and operational oversight',
                        userCount: users.filter(u => u.role === 'OPERATIONS_MANAGER').length,
                        permissions: getPermissionsByRole('operations_manager')
                      },
                      {
                        role: 'EMPLOYEE',
                        name: 'Employee',
                        description: 'Basic employee access and self-service',
                        userCount: users.filter(u => u.role === 'EMPLOYEE').length,
                        permissions: getPermissionsByRole('employee')
                      }
                    ].map((roleInfo) => (
                      <Card key={roleInfo.role} className="border-2">
                        <CardHeader className="pb-3">
                          <div className="flex justify-between items-start">
                            <div>
                              <CardTitle className="text-lg">{roleInfo.name}</CardTitle>
                              <Badge className={getRoleBadgeColor(roleInfo.role)} variant="secondary">
                                {roleInfo.userCount} users
                              </Badge>
                            </div>
                            <Dialog>
                              <DialogTrigger asChild>
                                <Button variant="outline" size="sm">
                                  <Settings className="h-4 w-4 mr-1" />
                                  Manage
                                </Button>
                              </DialogTrigger>
                              <DialogContent className="max-w-2xl">
                                <DialogHeader>
                                  <DialogTitle>Manage {roleInfo.name} Permissions</DialogTitle>
                                  <DialogDescription>
                                    Configure permissions for {roleInfo.name} role in the nightclub/restaurant HRMS
                                  </DialogDescription>
                                </DialogHeader>
                                <div className="space-y-4">
                                  <div className="text-sm text-muted-foreground">
                                    {roleInfo.description}
                                  </div>
                                  <div className="space-y-3">
                                    <h4 className="font-semibold">Current Permissions</h4>
                                    <div className="grid grid-cols-2 gap-2 max-h-64 overflow-y-auto border rounded p-3">
                                      {availablePermissions.map((permission) => (
                                        <div key={permission} className="flex items-center space-x-2">
                                          <Checkbox
                                            id={`role-permission-${roleInfo.role}-${permission}`}
                                            defaultChecked={roleInfo.permissions.includes(permission)}
                                          />
                                          <Label
                                            htmlFor={`role-permission-${roleInfo.role}-${permission}`}
                                            className="text-sm font-normal cursor-pointer"
                                          >
                                            {permission.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                                          </Label>
                                        </div>
                                      ))}
                                    </div>
                                  </div>
                                  <div className="flex justify-between items-center pt-4 border-t">
                                    <div className="text-sm text-muted-foreground">
                                      {roleInfo.permissions.length} permissions assigned
                                    </div>
                                    <Button onClick={() => {
                                      toast({
                                        title: "Role Updated",
                                        description: `Permissions for ${roleInfo.name} have been updated.`,
                                      });
                                    }}>
                                      Save Changes
                                    </Button>
                                  </div>
                                </div>
                              </DialogContent>
                            </Dialog>
                          </div>
                        </CardHeader>
                        <CardContent>
                          <p className="text-sm text-muted-foreground mb-3">{roleInfo.description}</p>
                          <div className="space-y-2">
                            <div className="text-sm font-medium">Key Permissions:</div>
                            <div className="flex flex-wrap gap-1">
                              {roleInfo.permissions.slice(0, 4).map((permission) => (
                                <Badge key={permission} variant="outline" className="text-xs">
                                  {permission.replace('_', ' ')}
                                </Badge>
                              ))}
                              {roleInfo.permissions.length > 4 && (
                                <Badge variant="outline" className="text-xs">
                                  +{roleInfo.permissions.length - 4} more
                                </Badge>
                              )}
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>

                  {/* Permission Matrix */}
                  <Card>
                    <CardHeader>
                      <CardTitle>Permission Matrix</CardTitle>
                      <CardDescription>Overview of permissions across all roles</CardDescription>
                    </CardHeader>
                    <CardContent>
                      <div className="overflow-x-auto">
                        <Table>
                          <TableHeader>
                            <TableRow>
                              <TableHead>Permission</TableHead>
                              <TableHead className="text-center">Admin</TableHead>
                              <TableHead className="text-center">HR Manager</TableHead>
                              <TableHead className="text-center">Operations Manager</TableHead>
                              <TableHead className="text-center">Employee</TableHead>
                            </TableRow>
                          </TableHeader>
                          <TableBody>
                            {availablePermissions.map((permission) => (
                              <TableRow key={permission}>
                                <TableCell className="font-medium">
                                  {permission.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase())}
                                </TableCell>
                                <TableCell className="text-center">
                                  {getPermissionsByRole('admin').includes(permission) ? (
                                    <UserCheck className="h-4 w-4 text-green-600 mx-auto" />
                                  ) : (
                                    <UserX className="h-4 w-4 text-gray-300 mx-auto" />
                                  )}
                                </TableCell>
                                <TableCell className="text-center">
                                  {getPermissionsByRole('hr_manager').includes(permission) ? (
                                    <UserCheck className="h-4 w-4 text-green-600 mx-auto" />
                                  ) : (
                                    <UserX className="h-4 w-4 text-gray-300 mx-auto" />
                                  )}
                                </TableCell>
                                <TableCell className="text-center">
                                  {getPermissionsByRole('operations_manager').includes(permission) ? (
                                    <UserCheck className="h-4 w-4 text-green-600 mx-auto" />
                                  ) : (
                                    <UserX className="h-4 w-4 text-gray-300 mx-auto" />
                                  )}
                                </TableCell>
                                <TableCell className="text-center">
                                  {getPermissionsByRole('employee').includes(permission) ? (
                                    <UserCheck className="h-4 w-4 text-green-600 mx-auto" />
                                  ) : (
                                    <UserX className="h-4 w-4 text-gray-300 mx-auto" />
                                  )}
                                </TableCell>
                              </TableRow>
                            ))}
                          </TableBody>
                        </Table>
                      </div>
                    </CardContent>
                  </Card>
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </DashboardLayout>
  );
};

export default UserManagement;

import { useState, useEffect, useMemo } from 'react';
import { DashboardLayout } from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Progress } from '@/components/ui/progress';
import { 
  Activity, 
  Clock, 
  CheckCircle, 
  XCircle, 
  Users, 
  Calendar, 
  DollarSign,
  TrendingUp,
  AlertTriangle
} from 'lucide-react';
import WorkflowStatusTracker from '@/components/WorkflowStatusTracker';
import { api } from '@/services/api';

const WorkflowDashboard = () => {
  const [leaveWorkflows, setLeaveWorkflows] = useState([]);
  const [salaryAdvanceWorkflows, setSalaryAdvanceWorkflows] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchWorkflows = async () => {
      try {
        setLoading(true);
        // Fetch leave requests
        const leaveRes = await api.leave.getLeaveRequests({ status: 'pending' });
        // Fetch salary advance requests
        const salaryRes = await api.salaryAdvances.getSalaryAdvanceRequests({ status: 'pending' });
        setLeaveWorkflows(
          (leaveRes?.data || []).map((item) => ({
            id: item.id,
            type: 'leave',
            requestId: item.id,
            currentStep: item.status,
            status: item.status,
            assignedTo: item.status === 'pending' ? 'operations' : '',
            createdAt: item.createdAt,
            updatedAt: item.updatedAt,
          }))
        );
        setSalaryAdvanceWorkflows(
          (salaryRes?.data || []).map((item) => ({
            id: item.id,
            type: 'salary_advance',
            requestId: item.id,
            currentStep: item.status,
            status: item.status,
            assignedTo: (item.status as string) === 'pending_ops' ? 'operations' : (item.status as string) === 'pending_hr' ? 'hr' : '',
            createdAt: item.createdAt,
            updatedAt: item.updatedAt,
          }))
        );
      } catch (error) {
        setLeaveWorkflows([]);
        setSalaryAdvanceWorkflows([]);
      } finally {
        setLoading(false);
      }
    };
    fetchWorkflows();
  }, []);

  const workflows = [...leaveWorkflows, ...salaryAdvanceWorkflows];

  const stats = useMemo(() => {
    // Only count pending_ops for leave, pending_ops and pending_hr for salary advance
    const totalActive = workflows.filter(w => !['approved', 'rejected', 'disbursed'].includes(w.status)).length;
    const pendingOps = workflows.filter(w => w.status === 'pending_ops').length;
    // Only salary advances can be pending_hr now
    const pendingHR = salaryAdvanceWorkflows.filter(w => w.status === 'pending_hr').length;
    const completedToday = workflows.filter(w => {
      const updatedDate = new Date(w.updatedAt);
      const today = new Date();
      return updatedDate.toDateString() === today.toDateString() && 
             ['approved', 'rejected', 'disbursed'].includes(w.status);
    }).length;

    return {
      totalActive,
      pendingOps,
      pendingHR,
      completedToday
    };
  }, [workflows, salaryAdvanceWorkflows]);

  // --- Workflow Performance Calculations ---
  const getAverageProcessingTime = (workflows) => {
    if (!workflows.length) return 0;
    // Only consider completed workflows
    const completed = workflows.filter(w => ['approved', 'rejected', 'disbursed'].includes(w.status));
    if (!completed.length) return 0;
    const totalMs = completed.reduce((sum, w) => {
      const created = new Date(w.createdAt).getTime();
      const updated = new Date(w.updatedAt).getTime();
      return sum + Math.max(0, updated - created);
    }, 0);
    const avgMs = totalMs / completed.length;
    return avgMs / (1000 * 60 * 60 * 24); // days
  };

  const getCompletionRate = (workflows) => {
    if (!workflows.length) return 0;
    const completed = workflows.filter(w => ['approved', 'rejected', 'disbursed'].includes(w.status)).length;
    return Math.round((completed / workflows.length) * 100);
  };

  const leaveAvgDays = getAverageProcessingTime(leaveWorkflows).toFixed(1);
  const leaveCompletion = getCompletionRate(leaveWorkflows);
  const salaryAvgDays = getAverageProcessingTime(salaryAdvanceWorkflows).toFixed(1);
  const salaryCompletion = getCompletionRate(salaryAdvanceWorkflows);

  const getWorkflowTypeIcon = (type: string) => {
    return type === 'leave' ? <Calendar className="h-4 w-4" /> : <DollarSign className="h-4 w-4" />;
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending_ops':
        return 'bg-yellow-100 text-yellow-800';
      case 'approved_ops':
        return 'bg-blue-100 text-blue-800';
      case 'pending_hr':
        return 'bg-purple-100 text-purple-800';
      case 'approved':
        return 'bg-green-100 text-green-800';
      case 'rejected':
        return 'bg-red-100 text-red-800';
      case 'disbursed':
        return 'bg-emerald-100 text-emerald-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const formatTimeAgo = (dateString: string) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${diffInHours}h ago`;
    return `${Math.floor(diffInHours / 24)}d ago`;
  };

  const overviewStats = [
    {
      title: 'Active Workflows',
      value: stats.totalActive.toString(),
      description: 'Currently in progress',
      icon: Activity,
      color: 'text-blue-600'
    },
    {
      title: 'Pending Operations',
      value: stats.pendingOps.toString(),
      description: 'Awaiting ops review',
      icon: Clock,
      color: 'text-yellow-600'
    },
    // Only show Pending HR if there are salary advances in that state
    ...(stats.pendingHR > 0 ? [{
      title: 'Pending HR',
      value: stats.pendingHR.toString(),
      description: 'Awaiting HR review (salary advance only)',
      icon: Users,
      color: 'text-purple-600'
    }] : []),
    {
      title: 'Completed Today',
      value: stats.completedToday.toString(),
      description: 'Finished workflows',
      icon: CheckCircle,
      color: 'text-green-600'
    }
  ];

  // --- Additional Calculations for Processing Trends and Bottlenecks ---
  // Helper to get start of week (Monday)
  const getStartOfWeek = (date) => {
    const d = new Date(date);
    const day = d.getDay();
    const diff = d.getDate() - day + (day === 0 ? -6 : 1); // adjust when day is sunday
    return new Date(d.setDate(diff));
  };
  // Helper to get start of last week
  const getStartOfLastWeek = (date) => {
    const startOfThisWeek = getStartOfWeek(date);
    return new Date(startOfThisWeek.setDate(startOfThisWeek.getDate() - 7));
  };
  // Get workflows completed in a given week
  const getCompletedInWeek = (workflows, weekStart) => {
    const weekEnd = new Date(weekStart);
    weekEnd.setDate(weekEnd.getDate() + 7);
    return workflows.filter(w => {
      const updated = new Date(w.updatedAt);
      return (
        ['approved', 'rejected', 'disbursed'].includes(w.status) &&
        updated >= weekStart && updated < weekEnd
      );
    });
  };
  // Calculate processing time for a set of workflows
  const getAvgProcessingTimeFor = (workflows) => {
    if (!workflows.length) return 0;
    const totalMs = workflows.reduce((sum, w) => {
      const created = new Date(w.createdAt).getTime();
      const updated = new Date(w.updatedAt).getTime();
      return sum + Math.max(0, updated - created);
    }, 0);
    return totalMs / workflows.length / (1000 * 60 * 60 * 24); // days
  };
  // Calculate approval rate for a set of workflows
  const getApprovalRateFor = (workflows) => {
    if (!workflows.length) return 0;
    const approved = workflows.filter(w => w.status === 'approved' || w.status === 'disbursed').length;
    return Math.round((approved / workflows.length) * 100);
  };
  // Calculate overdue workflows (pending for more than 3 days)
  const overdueThresholdDays = 3;
  const overdueCount = workflows.filter(w => {
    if (['approved', 'rejected', 'disbursed'].includes(w.status)) return false;
    const created = new Date(w.createdAt);
    const now = new Date();
    const diffDays = (now.getTime() - created.getTime()) / (1000 * 60 * 60 * 24);
    return diffDays > overdueThresholdDays;
  }).length;

  // Calculate trends for this week vs last week
  const now = new Date();
  const startOfThisWeek = getStartOfWeek(now);
  const startOfLastWeek = getStartOfLastWeek(now);
  const completedThisWeek = getCompletedInWeek(workflows, startOfThisWeek);
  const completedLastWeek = getCompletedInWeek(workflows, startOfLastWeek);
  const avgTimeThisWeek = getAvgProcessingTimeFor(completedThisWeek);
  const avgTimeLastWeek = getAvgProcessingTimeFor(completedLastWeek);
  let percentFaster = 0;
  if (avgTimeLastWeek > 0 && avgTimeThisWeek > 0) {
    percentFaster = Math.round(((avgTimeLastWeek - avgTimeThisWeek) / avgTimeLastWeek) * 100);
  }
  const approvalRate = getApprovalRateFor(workflows);
  const avgProcessingTime = getAverageProcessingTime(workflows).toFixed(1);

  return (
    <DashboardLayout title="Workflow Management">
      <div className="space-y-6">
        {/* Overview Stats */}
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          {overviewStats.map((stat) => (
            <Card key={stat.title}>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">{stat.title}</CardTitle>
                <stat.icon className={`h-4 w-4 ${stat.color}`} />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{stat.value}</div>
                <p className="text-xs text-muted-foreground">{stat.description}</p>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Workflow Performance Chart */}
        <Card>
          <CardHeader>
            <CardTitle>Workflow Performance</CardTitle>
            <CardDescription>Average processing time and completion rates</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div className="space-y-1">
                  <p className="text-sm font-medium">Leave Requests</p>
                  <p className="text-xs text-muted-foreground">Avg. {leaveAvgDays} days processing time</p>
                </div>
                <div className="flex items-center gap-2">
                  <Progress value={leaveCompletion} className="w-20" />
                  <span className="text-sm font-medium">{leaveCompletion}%</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <div className="space-y-1">
                  <p className="text-sm font-medium">Salary Advances</p>
                  <p className="text-xs text-muted-foreground">Avg. {salaryAvgDays} days processing time</p>
                </div>
                <div className="flex items-center gap-2">
                  <Progress value={salaryCompletion} className="w-20" />
                  <span className="text-sm font-medium">{salaryCompletion}%</span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Active Workflows */}
        <Tabs defaultValue="all" className="space-y-4">
          <TabsList>
            <TabsTrigger value="all">All Workflows ({workflows.length})</TabsTrigger>
            <TabsTrigger value="leave">Leave Requests ({leaveWorkflows.length})</TabsTrigger>
            <TabsTrigger value="salary">Salary Advances ({salaryAdvanceWorkflows.length})</TabsTrigger>
          </TabsList>

          <TabsContent value="all" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>All Active Workflows</CardTitle>
                <CardDescription>Monitor all ongoing workflow processes</CardDescription>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Type</TableHead>
                      <TableHead>Request ID</TableHead>
                      <TableHead>Current Step</TableHead>
                      <TableHead>Assigned To</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {workflows.map((workflow) => (
                      <TableRow key={workflow.id}>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            {getWorkflowTypeIcon(workflow.type)}
                            <span className="capitalize">{workflow.type.replace('_', ' ')}</span>
                          </div>
                        </TableCell>
                        <TableCell className="font-medium">#{workflow.requestId}</TableCell>
                        <TableCell className="capitalize">{workflow.currentStep.replace('_', ' ')}</TableCell>
                        <TableCell className="capitalize">{workflow.assignedTo?.replace('_', ' ')}</TableCell>
                        <TableCell>
                          <Badge className={getStatusColor(workflow.status)}>
                            {workflow.status.replace('_', ' ')}
                          </Badge>
                        </TableCell>
                        <TableCell>{formatTimeAgo(workflow.createdAt)}</TableCell>
                        <TableCell>
                          <Button variant="outline" size="sm">
                            View Details
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                {workflows.length === 0 && (
                  <div className="text-center py-8 text-muted-foreground">
                    No active workflows found
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="leave" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Leave Request Workflows</CardTitle>
                <CardDescription>Track leave request processing</CardDescription>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Request ID</TableHead>
                      <TableHead>Current Step</TableHead>
                      <TableHead>Assigned To</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {leaveWorkflows.map((workflow) => (
                      <TableRow key={workflow.id}>
                        <TableCell className="font-medium">#{workflow.requestId}</TableCell>
                        <TableCell className="capitalize">{workflow.currentStep.replace('_', ' ')}</TableCell>
                        <TableCell className="capitalize">{workflow.assignedTo?.replace('_', ' ')}</TableCell>
                        <TableCell>
                          <Badge className={getStatusColor(workflow.status)}>
                            {workflow.status.replace('_', ' ')}
                          </Badge>
                        </TableCell>
                        <TableCell>{formatTimeAgo(workflow.createdAt)}</TableCell>
                        <TableCell>
                          <Button variant="outline" size="sm">
                            View Details
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                {leaveWorkflows.length === 0 && (
                  <div className="text-center py-8 text-muted-foreground">
                    No active leave workflows found
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="salary" className="space-y-4">
            <Card>
              <CardHeader>
                <CardTitle>Salary Advance Workflows</CardTitle>
                <CardDescription>Track salary advance processing</CardDescription>
              </CardHeader>
              <CardContent>
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Request ID</TableHead>
                      <TableHead>Current Step</TableHead>
                      <TableHead>Assigned To</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Created</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {salaryAdvanceWorkflows.map((workflow) => (
                      <TableRow key={workflow.id}>
                        <TableCell className="font-medium">#{workflow.requestId}</TableCell>
                        <TableCell className="capitalize">{workflow.currentStep.replace('_', ' ')}</TableCell>
                        <TableCell className="capitalize">{workflow.assignedTo?.replace('_', ' ')}</TableCell>
                        <TableCell>
                          <Badge className={getStatusColor(workflow.status)}>
                            {workflow.status.replace('_', ' ')}
                          </Badge>
                        </TableCell>
                        <TableCell>{formatTimeAgo(workflow.createdAt)}</TableCell>
                        <TableCell>
                          <Button variant="outline" size="sm">
                            View Details
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
                {salaryAdvanceWorkflows.length === 0 && (
                  <div className="text-center py-8 text-muted-foreground">
                    No active salary advance workflows found
                  </div>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>

        {/* Workflow Insights */}
        <div className="grid gap-4 md:grid-cols-2">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <TrendingUp className="h-5 w-5" />
                Processing Trends
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm">This Week</span>
                  <span className="text-sm font-medium">{percentFaster > 0 ? `+${percentFaster}% faster` : percentFaster < 0 ? `${percentFaster}% slower` : 'No change'}</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm">Approval Rate</span>
                  <span className="text-sm font-medium">{approvalRate}%</span>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm">Avg. Processing Time</span>
                  <span className="text-sm font-medium">{avgProcessingTime} days</span>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <AlertTriangle className="h-5 w-5" />
                Bottlenecks
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex justify-between items-center">
                  <span className="text-sm">HR Review Queue</span>
                  <Badge variant="secondary">{stats.pendingHR} pending</Badge>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm">Operations Queue</span>
                  <Badge variant="secondary">{stats.pendingOps} pending</Badge>
                </div>
                <div className="flex justify-between items-center">
                  <span className="text-sm">Overdue Reviews</span>
                  <Badge variant="destructive">{overdueCount} overdue</Badge>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </DashboardLayout>
  );
};

export default WorkflowDashboard;

import { DashboardLayout } from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { useState, useMemo } from 'react';
import { Plus, Edit, Trash2, Building2, Users, DollarSign, Loader2, AlertTriangle } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { Department } from '@/types/department';
import { useAuth } from '@/contexts/AuthContext';
import { canManageDepartments } from '@/utils/permissions';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { departmentService, employeeService } from '@/services/api';
import { userService } from '@/services/api/user.service';
import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { extractDataFromResponse } from '@/utils/api-helpers';
import { User } from '@/types/types';

export interface CreateDepartmentRequest {
  name: string;
  description?: string;
  managerId?: string;
}

export interface UpdateDepartmentRequest {
  name?: string;
  description?: string;
  managerId?: string;
  status?: string;
}

const DepartmentManagement = () => {
  const { toast } = useToast();
  const { user } = useAuth();
  const queryClient = useQueryClient();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [selectedDepartment, setSelectedDepartment] = useState<Department | null>(null);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');

  const [newDepartment, setNewDepartment] = useState<CreateDepartmentRequest>({
    name: '',
    description: '',
    managerId: undefined
  });

  const [editDepartment, setEditDepartment] = useState<UpdateDepartmentRequest>({
    name: '',
    description: '',
    managerId: undefined,
    status: 'ACTIVE'
  });

  // Fetch departments
  const {
    data: departments = [],
    isLoading: isLoadingDepartments,
    isError: isDepartmentsError,
    error: departmentsError
  } = useQuery({
    queryKey: ['departments'],
    queryFn: () => departmentService.getAllDepartments(),
  });

  // Fetch employees for manager selection
  const {
    data: employees = [],
    isLoading: isLoadingEmployees,
    isError: isEmployeesError
  } = useQuery({
    queryKey: ['employees'],
    queryFn: () => employeeService.getEmployees(),
  });

  // Fetch users for manager selection
  const {
    data: usersData = [],
    isLoading: isLoadingUsers,
    isError: isUsersError
  } = useQuery({
    queryKey: ['users'],
    queryFn: () => userService.getUsers({ status: 'active' }),
  });

  // Extract data from possible paginated responses
  const employeesList = extractDataFromResponse(employees);

  // Extract users and map to employees for display
  let usersList: User[] = [];
  if (Array.isArray(usersData)) {
    usersList = usersData;
  } else if (usersData && Array.isArray(usersData.data)) {
    usersList = usersData.data;
  }

  // Enhanced manager options with multiple fallback strategies
  const managerOptions = useMemo(() => {
    console.log('Creating manager options...');
    console.log('Users Data:', usersData);
    console.log('Users List:', usersList);
    console.log('Employees List:', employeesList);

    if (!usersList || !employeesList) {
      console.log('Missing data - returning empty options');
      return [];
    }

    const options: Array<{userId: string; employeeName: string; source: string}> = [];

    // Strategy 1: Users with linked employees
    usersList.forEach((user: User) => {
      if (user.employeeId) {
        const emp = employeesList.find((e) => e.id === user.employeeId);
        if (emp && emp.firstName && emp.lastName) {
          options.push({
            userId: user.id,
            employeeName: `${emp.firstName} ${emp.lastName}`,
            source: 'user-employee-link'
          });
        }
      }
    });

    // Strategy 2: Users with names (fallback)
    if (options.length === 0) {
      usersList.forEach((user: User) => {
        if (user.firstName && user.lastName) {
          options.push({
            userId: user.id,
            employeeName: `${user.firstName} ${user.lastName}`,
            source: 'user-direct'
          });
        }
      });
    }

    // Strategy 3: All users regardless of names (last resort)
    if (options.length === 0) {
      usersList.forEach((user: User) => {
        const name = [user.firstName, user.lastName].filter(Boolean).join(' ') || user.email;
        options.push({
          userId: user.id,
          employeeName: name,
          source: 'user-fallback'
        });
      });
    }

    console.log('Final manager options:', options);
    return options;
  }, [usersList, employeesList, usersData]);

  // Debug info
  console.log('Debug Info:', {
    selectedDepartment,
    editDepartment,
    usersList: usersList.slice(0, 3),
    employeesList: employeesList.slice(0, 3),
    managerOptions: managerOptions.slice(0, 3),
    isLoadingUsers,
    isUsersError,
    isLoadingEmployees,
    isEmployeesError,
    totalManagerOptions: managerOptions.length,
    totalUsers: usersList.length,
    totalEmployees: employeesList.length
  });
  const createDepartmentMutation = useMutation({
    mutationFn: (newDept: CreateDepartmentRequest) => 
      departmentService.createDepartment(newDept),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['departments'] });
      setIsAddDialogOpen(false);
      setNewDepartment({
        name: '',
        description: '',
        managerId: undefined
      });
      toast({
        title: 'Department created',
        description: 'The department has been created successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Failed to create department',
        description: error.message || 'An error occurred while creating the department.',
        variant: 'destructive',
      });
    }
  });

  // Update department mutation
  const updateDepartmentMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: UpdateDepartmentRequest }) => 
      departmentService.updateDepartment(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['departments'] });
      setIsEditDialogOpen(false);
      setSelectedDepartment(null);
      toast({
        title: 'Department updated',
        description: 'The department has been updated successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Failed to update department',
        description: error.message || 'An error occurred while updating the department.',
        variant: 'destructive',
      });
    }
  });

  // Delete department mutation
  const deleteDepartmentMutation = useMutation({
    mutationFn: (id: string) => departmentService.deleteDepartment(id),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['departments'] });
      toast({
        title: 'Department deleted',
        description: 'The department has been deleted successfully.',
      });
    },
    onError: (error: any) => {
      toast({
        title: 'Failed to delete department',
        description: error.message || 'An error occurred while deleting the department.',
        variant: 'destructive',
      });
    }
  });

  const handleCreateDepartment = () => {
    createDepartmentMutation.mutate(newDepartment);
  };

  const handleUpdateDepartment = async () => {
    if (!selectedDepartment) {
      toast({
        title: 'Error',
        description: 'No department selected for update.',
        variant: 'destructive'
      });
      return;
    }

    console.log('Updating department:', selectedDepartment.id, 'with data:', editDepartment);
    
    try {
      await updateDepartmentMutation.mutateAsync({
        id: selectedDepartment.id,
        data: editDepartment
      });
    } catch (error) {
      console.error('Department update error:', error);
    }
  };

  const handleDeleteDepartment = (id: string) => {
    if (window.confirm('Are you sure you want to delete this department? This action cannot be undone.')) {
      deleteDepartmentMutation.mutate(id);
    }
  };

  const openEditDialog = (department: Department) => {
    setSelectedDepartment(department);
    setEditDepartment({
      name: department.name,
      description: department.description || '',
      managerId: department.managerId || 'none', // Convert null/undefined to 'none'
      status: department.status || 'ACTIVE'
    });
    setIsEditDialogOpen(true);
  };

  // Filter departments based on search term
  const filteredDepartments = departments.filter((dept: Department) => 
    dept.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    (dept.description && dept.description.toLowerCase().includes(searchTerm.toLowerCase()))
  );

  // Check if user has permission to manage departments
  const canManage = canManageDepartments(user);

  // If user doesn't have permission, show alert
  if (!canManage) {
    return (
      <DashboardLayout title="Department Management">
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertTitle>Access Denied</AlertTitle>
          <AlertDescription>
            You don't have permission to manage departments. Please contact your administrator.
          </AlertDescription>
        </Alert>
      </DashboardLayout>
    );
  }

  console.log('Debug Info:', {
    selectedDepartment,
    editDepartment,
    usersList: usersList.slice(0, 3), // First 3 users
    employeesList: employeesList.slice(0, 3), // First 3 employees
    managerOptions: managerOptions.slice(0, 3), // First 3 options
    isLoadingUsers,
    isUsersError,
    usersData: typeof usersData,
    totalManagerOptions: managerOptions.length
  });
  
  console.log('Employees List:', employeesList);
  console.log('Users List:', usersList);
  console.log('Manager Options:', managerOptions);
  console.log('Users with employeeId:', usersList.filter((user: User) => user.employeeId));
  console.log('Employees:', employeesList);

  return (
    <DashboardLayout title="Department Management">
      <Card className="mb-6">
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Departments</CardTitle>
              <CardDescription>Manage your organization's departments</CardDescription>
            </div>
            <Button onClick={() => setIsAddDialogOpen(true)} className="bg-green-600 hover:bg-green-700 text-white">
              <Plus className="mr-2 h-4 w-4 text-white" />
              Add Department
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          <div className="mb-4">
            <Input
              placeholder="Search departments..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="max-w-sm"
            />
          </div>

          {isLoadingDepartments ? (
            <div className="flex justify-center items-center h-40">
              <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
            </div>
          ) : isDepartmentsError ? (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertTitle>Error</AlertTitle>
              <AlertDescription>
                {(departmentsError as Error)?.message || 'Failed to load departments'}
              </AlertDescription>
            </Alert>
          ) : filteredDepartments.length === 0 ? (
            <div className="text-center py-8 text-muted-foreground">
              No departments found. {searchTerm ? 'Try a different search term.' : 'Add a department to get started.'}
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Description</TableHead>
                  <TableHead>Manager</TableHead>
                  <TableHead>Employees</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead className="text-right">Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredDepartments.map((department: Department) => {
                  // Find the manager in usersList by user.id
                  const manager = usersList.find(user => user.id === department.managerId);
                  return (
                    <TableRow key={department.id}>
                      <TableCell className="font-medium">{department.name}</TableCell>
                      <TableCell>
                        {department.description ? 
                          department.description.length > 50 
                            ? `${department.description.substring(0, 50)}...` 
                            : department.description 
                          : 'No description'}
                      </TableCell>
                      <TableCell>
                        {manager 
                          ? `${manager.firstName} ${manager.lastName}`
                          : 'None'}
                      </TableCell>
                      <TableCell>
                        {department.employees?.length || 0}
                      </TableCell>
                      <TableCell>
                        <Badge
                          variant={department.status === 'ACTIVE' ? 'default' : 'secondary'}
                        >
                          {department.status || 'ACTIVE'}
                        </Badge>
                      </TableCell>
                      <TableCell className="text-right">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => openEditDialog(department)}
                          className="hover:bg-blue-100"
                        >
                          <Edit className="h-4 w-4 text-blue-600" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleDeleteDepartment(department.id)}
                          disabled={deleteDepartmentMutation.isPending}
                          className="hover:bg-red-100"
                        >
                          <Trash2 className="h-4 w-4 text-red-600" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Add Department Dialog */}
      <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Add Department</DialogTitle>
            <DialogDescription>
              Create a new department in your organization.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="name">Department Name *</Label>
              <Input
                id="name"
                placeholder="Enter department name"
                value={newDepartment.name}
                onChange={(e) => setNewDepartment({ ...newDepartment, name: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                placeholder="Enter department description"
                value={newDepartment.description || ''}
                onChange={(e) => setNewDepartment({ ...newDepartment, description: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="manager">Department Manager</Label>
              <Select
                value={newDepartment.managerId || 'none'}
                onValueChange={(value) => setNewDepartment({ ...newDepartment, managerId: value === 'none' ? undefined : value })}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a manager" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">None</SelectItem>
                  {isLoadingUsers ? (
                    <div className="flex items-center justify-center p-2">
                      <Loader2 className="h-4 w-4 animate-spin" />
                    </div>
                  ) : isUsersError ? (
                    <div className="flex items-center justify-center p-2 text-red-500">
                      Error loading users
                    </div>
                  ) : managerOptions.length === 0 ? (
                    <div className="flex items-center justify-center p-2 text-gray-500">
                      No managers available
                    </div>
                  ) : (
                    managerOptions.map((option) => (
                      <SelectItem key={option.userId} value={option.userId}>
                        {option.employeeName}
                      </SelectItem>
                    ))
                  )}
                </SelectContent>
              </Select>
            </div>
          </div>
          <div className="flex justify-end space-x-2">
            <Button variant="outline" onClick={() => setIsAddDialogOpen(false)} className="border-gray-400 text-gray-700 hover:bg-gray-100">Cancel</Button>
            <Button onClick={handleCreateDepartment} disabled={!newDepartment.name || createDepartmentMutation.isPending} className="bg-green-600 hover:bg-green-700 text-white">
              {createDepartmentMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin text-white" />}
              Create Department
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Edit Department Dialog */}
      <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle>Edit Department</DialogTitle>
            <DialogDescription>
              Update department information.
            </DialogDescription>
          </DialogHeader>
          <div className="space-y-4 py-4">
            <div className="space-y-2">
              <Label htmlFor="edit-name">Department Name *</Label>
              <Input
                id="edit-name"
                placeholder="Enter department name"
                value={editDepartment.name || ''}
                onChange={(e) => setEditDepartment({ ...editDepartment, name: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="edit-description">Description</Label>
              <Textarea
                id="edit-description"
                placeholder="Enter department description"
                value={editDepartment.description || ''}
                onChange={(e) => setEditDepartment({ ...editDepartment, description: e.target.value })}
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="edit-manager">Department Manager</Label>
              <Select
                value={editDepartment.managerId || 'none'}
                onValueChange={(value) => setEditDepartment({ ...editDepartment, managerId: value === 'none' ? undefined : value })}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select a manager" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="none">None</SelectItem>
                  {isLoadingUsers ? (
                    <div className="flex items-center justify-center p-2">
                      <Loader2 className="h-4 w-4 animate-spin" />
                    </div>
                  ) : isUsersError ? (
                    <div className="flex items-center justify-center p-2 text-red-500">
                      Error loading users
                    </div>
                  ) : managerOptions.length === 0 ? (
                    <div className="flex items-center justify-center p-2 text-gray-500">
                      No managers available
                    </div>
                  ) : (
                    managerOptions.map((option) => (
                      <SelectItem key={option.userId} value={option.userId}>
                        {option.employeeName}
                      </SelectItem>
                    ))
                  )}
                </SelectContent>
              </Select>
            </div>
            <div className="space-y-2">
              <Label htmlFor="edit-status">Status</Label>
              <Select
                value={editDepartment.status || 'ACTIVE'}
                onValueChange={(value) => setEditDepartment({ ...editDepartment, status: value })}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Select status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="ACTIVE">Active</SelectItem>
                  <SelectItem value="INACTIVE">Inactive</SelectItem>
                </SelectContent>
              </Select>
            </div>
          </div>
          <div className="flex justify-end space-x-2">
            <Button variant="outline" onClick={() => setIsEditDialogOpen(false)} className="border-gray-400 text-gray-700 hover:bg-gray-100">Cancel</Button>
            <Button onClick={handleUpdateDepartment} disabled={!editDepartment.name || updateDepartmentMutation.isPending} className="bg-blue-600 hover:bg-blue-700 text-white">
              {updateDepartmentMutation.isPending && <Loader2 className="mr-2 h-4 w-4 animate-spin text-white" />}
              Update Department
            </Button>
          </div>
        </DialogContent>
      </Dialog>

      {/* Department Statistics */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-6">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Departments</CardTitle>
            <Building2 className="h-4 w-4 text-green-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{departments.length}</div>
            <p className="text-xs text-muted-foreground">Across the organization</p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Active Departments</CardTitle>
            <Users className="h-4 w-4 text-blue-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {departments.filter(d => d.status === 'ACTIVE' || !d.status).length}
            </div>
            <p className="text-xs text-muted-foreground">Currently operational</p>
          </CardContent>
        </Card>
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Employees</CardTitle>
            <Users className="h-4 w-4 text-yellow-600" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {departments.reduce((total, dept) => total + (dept.employees?.length || 0), 0)}
            </div>
            <p className="text-xs text-muted-foreground">Assigned to departments</p>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
};

export default DepartmentManagement;

import { DashboardLayout } from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Users, 
  Search, 
  Filter, 
  Mail, 
  Phone, 
  Calendar, 
  MapPin, 
  Clock,
  UserCheck,
  UserX,
  MoreVertical,
  Eye,
  Edit,
  MessageSquare,
  Shield,
  Info,
  UserPlus
} from 'lucide-react';
import { useState, useMemo, useEffect } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import { employeeService } from '@/services/api/employee.service';
import { User } from '@/types/types';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { canAddEmployee, canManageTeamMembers, getRolePermissionsDescription, getRoleDisplayName } from '@/utils/permissions';

const TeamsOverview = () => {
  const { user } = useAuth();
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [departmentFilter, setDepartmentFilter] = useState('all');
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    setLoading(true);
    setError(null);
    if (user?.branch) {
      const userBranch = user && user.branch != null ? (typeof user.branch === 'object' ? (user.branch as any).name : user.branch) : '';
      employeeService.getEmployeesByBranch(userBranch)
        .then((members) => {
          const usersWithRoles = members.map((member) => ({
            ...member,
            role: 'EMPLOYEE' as const,
          }));
          setUsers(usersWithRoles);
          setLoading(false);
        })
        .catch(() => {
          setError('Failed to fetch team members.');
          setUsers([]);
          setLoading(false);
        });
    } else {
      setUsers([]);
      setLoading(false);
    }
  }, [user?.branch]);

  const canAdd = canAddEmployee(user);
  const canManage = canManageTeamMembers(user);

  const branchEmployees = useMemo(() => {
    const userBranch = user && user.branch != null ? (typeof user.branch === 'object' ? (user.branch as any).name : user.branch) : '';
    return users.filter(
      (employee) => 
        employee.branch === userBranch && 
        employee.role === 'EMPLOYEE'
    );
  }, [user?.branch, users]);

  const filteredEmployees = useMemo(() => {
    return branchEmployees.filter((employee) => {
      const matchesSearch = 
        employee.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.employeeId.toLowerCase().includes(searchTerm.toLowerCase()) ||
        employee.email.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesStatus = statusFilter === 'all' || employee.status === statusFilter;
      const matchesDepartment = departmentFilter === 'all' || employee.department === departmentFilter;
      
      return matchesSearch && matchesStatus && matchesDepartment;
    });
  }, [branchEmployees, searchTerm, statusFilter, departmentFilter]);

  const departments = useMemo(() => {
    const depts = [...new Set(branchEmployees.map(emp => emp.department).filter(Boolean))];
    return depts;
  }, [branchEmployees]);

  const teamStats = useMemo(() => {
    const total = branchEmployees.length;
    const active = branchEmployees.filter(emp => emp.status === 'active').length;
    const inactive = branchEmployees.filter(emp => emp.status === 'inactive').length;
    const recentlyActive = branchEmployees.filter(emp => {
      if (!emp.lastLogin) return false;
      const lastLogin = new Date(emp.lastLogin);
      const oneDayAgo = new Date();
      oneDayAgo.setDate(oneDayAgo.getDate() - 1);
      return lastLogin > oneDayAgo;
    }).length;

    return { total, active, inactive, recentlyActive };
  }, [branchEmployees]);

  const getInitials = (firstName: string, lastName: string) => {
    return `${firstName.charAt(0)}${lastName.charAt(0)}`.toUpperCase();
  };

  const getStatusColor = (status?: string) => {
    switch (status) {
      case 'active':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'inactive':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      case 'suspended':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  const formatLastLogin = (lastLogin?: string) => {
    if (!lastLogin) return 'Never';
    const date = new Date(lastLogin);
    const now = new Date();
    const diffInHours = Math.floor((now.getTime() - date.getTime()) / (1000 * 60 * 60));
    
    if (diffInHours < 1) return 'Just now';
    if (diffInHours < 24) return `${diffInHours}h ago`;
    if (diffInHours < 48) return 'Yesterday';
    return date.toLocaleDateString();
  };

  const EmployeeCard = ({ employee }: { employee: User }) => (
    <Card className="hover:shadow-md transition-shadow">
      <CardContent className="p-6">
        <div className="flex items-start justify-between">
          <div className="flex items-start space-x-4">
            <Avatar className="h-12 w-12">
              <AvatarImage src={employee.profilePicture} />
              <AvatarFallback className="bg-primary/10 text-primary font-semibold">
                {getInitials(employee.firstName, employee.lastName)}
