                </h3>
                <Badge className={getStatusColor(employee.status)}>
                  {employee.status || 'active'}
                </Badge>
              </div>
              <p className="text-sm text-muted-foreground mb-1">
                {employee.position || 'Team Member'}  {employee.employeeId}
              </p>
              <p className="text-sm text-muted-foreground mb-3">
                {employee.department}
              </p>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-2 text-sm">
                <div className="flex items-center space-x-2 text-muted-foreground">
                  <Mail className="h-4 w-4" />
                  <span className="truncate">{employee.email}</span>
                </div>
                {employee.phone && (
                  <div className="flex items-center space-x-2 text-muted-foreground">
                    <Phone className="h-4 w-4" />
                    <span>{employee.phone}</span>
                  </div>
                )}
                <div className="flex items-center space-x-2 text-muted-foreground">
                  <Calendar className="h-4 w-4" />
                  <span>Hired: {employee.hireDate ? new Date(employee.hireDate).toLocaleDateString() : 'N/A'}</span>
                </div>
                <div className="flex items-center space-x-2 text-muted-foreground">
                  <Clock className="h-4 w-4" />
                  <span>Last seen: {formatLastLogin(employee.lastLogin)}</span>
                </div>
              </div>
            </div>
          </div>
          
          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button variant="ghost" size="sm">
                <MoreVertical className="h-4 w-4" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="end">
              <DropdownMenuItem>
                <Eye className="h-4 w-4 mr-2" />
                View Profile
              </DropdownMenuItem>
              <DropdownMenuItem>
                <MessageSquare className="h-4 w-4 mr-2" />
                Send Message
              </DropdownMenuItem>
              <DropdownMenuItem>
                <Edit className="h-4 w-4 mr-2" />
                Edit Details
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>
        </div>
      </CardContent>
    </Card>
  );

  const EmployeeTable = ({ employees }: { employees: User[] }) => (
    <div className="border rounded-lg">
      <div className="overflow-x-auto">
        <table className="w-full">
          <thead className="border-b bg-muted/50">
            <tr>
              <th className="text-left p-4 font-medium">Employee</th>
              <th className="text-left p-4 font-medium">Position</th>
              <th className="text-left p-4 font-medium">Department</th>
              <th className="text-left p-4 font-medium">Status</th>
              <th className="text-left p-4 font-medium">Last Login</th>
              <th className="text-left p-4 font-medium">Actions</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((employee) => (
              <tr key={employee.id} className="border-b hover:bg-muted/50">
                <td className="p-4">
                  <div className="flex items-center space-x-3">
                    <Avatar className="h-8 w-8">
                      <AvatarImage src={employee.profilePicture} />
                      <AvatarFallback className="bg-primary/10 text-primary text-xs">
                        {getInitials(employee.firstName, employee.lastName)}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <p className="font-medium">{employee.firstName} {employee.lastName}</p>
                      <p className="text-sm text-muted-foreground">{employee.employeeId}</p>
                    </div>
                  </div>
                </td>
                <td className="p-4 text-sm">{employee.position || 'Team Member'}</td>
                <td className="p-4 text-sm">{employee.department}</td>
                <td className="p-4">
                  <Badge className={getStatusColor(employee.status)}>
                    {employee.status || 'active'}
                  </Badge>
                </td>
                <td className="p-4 text-sm">{formatLastLogin(employee.lastLogin)}</td>
                <td className="p-4">
                  <DropdownMenu>
                    <DropdownMenuTrigger asChild>
                      <Button variant="ghost" size="sm">
                        <MoreVertical className="h-4 w-4" />
                      </Button>
                    </DropdownMenuTrigger>
                    <DropdownMenuContent align="end">
                      <DropdownMenuItem>
                        <Eye className="h-4 w-4 mr-2" />
                        View Profile
                      </DropdownMenuItem>
                      <DropdownMenuItem>
                        <MessageSquare className="h-4 w-4 mr-2" />
                        Send Message
                      </DropdownMenuItem>
                      <DropdownMenuItem>
                        <Edit className="h-4 w-4 mr-2" />
                        Edit Details
                      </DropdownMenuItem>
                    </DropdownMenuContent>
                  </DropdownMenu>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  const filteredUsers = useMemo(() => {
    return users.filter(u => {
      const matchesSearch = `${u.firstName} ${u.lastName}`.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesStatus = statusFilter === 'all' || u.status === statusFilter;
      const matchesDepartment = departmentFilter === 'all' || u.department === departmentFilter;
      return matchesSearch && matchesStatus && matchesDepartment && u.role === 'EMPLOYEE';
    });
  }, [users, searchTerm, statusFilter, departmentFilter]);

  return (
    <DashboardLayout title="Teams Overview">
      <div className="space-y-6">
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
          <div>
            <p className="text-muted-foreground">
              Manage and monitor your team at {user && user.branch != null ? (typeof user.branch === 'object' ? (user.branch as any).name || 'Unknown' : user.branch || 'Unknown') : 'Unknown'} branch
            </p>
          </div>
          {canAdd ? (
            <Button>
              <UserPlus className="h-4 w-4 mr-2" />
              Add Team Member
            </Button>
          ) : null}
        </div>

        
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-4">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Team Members</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{teamStats.total}</div>
              <p className="text-xs text-muted-foreground">
                Under your supervision
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Active Members</CardTitle>
              <UserCheck className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{teamStats.active}</div>
              <p className="text-xs text-muted-foreground">
                Currently active
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Inactive Members</CardTitle>
              <UserX className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{teamStats.inactive}</div>
              <p className="text-xs text-muted-foreground">
                Need attention
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Recently Active</CardTitle>
              <Clock className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-blue-600">{teamStats.recentlyActive}</div>
              <p className="text-xs text-muted-foreground">
                Last 24 hours
              </p>
            </CardContent>
          </Card>
        </div>

        
        <Card>
          <CardHeader>
            <CardTitle>Team Members</CardTitle>
            <CardDescription>
              Search and filter your team members
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col sm:flex-row gap-4 mb-6">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search by name, ID, or email..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger className="w-full sm:w-[180px]">
                  <SelectValue placeholder="Filter by status" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Status</SelectItem>
                  <SelectItem value="active">Active</SelectItem>
                  <SelectItem value="inactive">Inactive</SelectItem>
                  <SelectItem value="suspended">Suspended</SelectItem>
                </SelectContent>
              </Select>
              <Select value={departmentFilter} onValueChange={setDepartmentFilter}>
                <SelectTrigger className="w-full sm:w-[180px]">
                  <SelectValue placeholder="Filter by department" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Departments</SelectItem>
                  {departments.map((dept) => (
                    <SelectItem key={dept} value={dept}>{dept}</SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <Tabs defaultValue="cards" className="w-full">
              <TabsList className="grid w-full grid-cols-2 max-w-[400px]">
                <TabsTrigger value="cards">Card View</TabsTrigger>
                <TabsTrigger value="table">Table View</TabsTrigger>
              </TabsList>
              
              <TabsContent value="cards" className="mt-6">
                {filteredEmployees.length === 0 ? (
                  <div className="text-center py-12">
                    <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No team members found</h3>
                    <p className="text-muted-foreground">
                      {searchTerm || statusFilter !== 'all' || departmentFilter !== 'all'
                        ? 'Try adjusting your search or filters'
                        : 'No team members assigned to your branch yet'}
                    </p>
                  </div>
                ) : (
                  <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-1 xl:grid-cols-2">
                    {filteredEmployees.map((employee) => (
                      <EmployeeCard key={employee.id} employee={employee} />
                    ))}
                  </div>
                )}
              </TabsContent>
              
              <TabsContent value="table" className="mt-6">
                {filteredEmployees.length === 0 ? (
                  <div className="text-center py-12">
                    <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-semibold mb-2">No team members found</h3>
                    <p className="text-muted-foreground">
                      {searchTerm || statusFilter !== 'all' || departmentFilter !== 'all'
                        ? 'Try adjusting your search or filters'
                        : 'No team members assigned to your branch yet'}
                    </p>
                  </div>
                ) : (
                  <EmployeeTable employees={filteredEmployees} />
                )}
              </TabsContent>
            </Tabs>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
};

export default TeamsOverview;

import { useState, useEffect } from 'react';
import { DashboardLayout } from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { 
  Users, 
  DollarSign, 
  Edit,
  Save,
  RefreshCw,
  AlertTriangle,
  Search
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { employeeService, type Employee } from '@/services/api/employee.service';
import PayrollDataService from '@/services/payrollDataService';
import { PayrollEngine } from '@/services/payrollEngine';
import type { KenyanPayrollEmployee } from '@/types/payroll';

// Enhanced employee type with Kenyan payroll information
interface KenyanEmployee extends Employee {
  payrollInfo: {
    employeeType: 'hourly' | 'salaried';
    monthlySalary?: number;
    hourlyRate?: number;
    overtimeRate?: number;
    personalRelief: number;
    kraPin?: string;
    nssfNumber?: string;
    nhifNumber?: string;
    bankAccount?: {
      bankName: string;
      accountNumber: string;
      branchCode: string;
    };
  };
}

const EmployeeCompensation = () => {
  const { toast } = useToast();
  const [employees, setEmployees] = useState<KenyanEmployee[]>([]);
  const [filteredEmployees, setFilteredEmployees] = useState<KenyanEmployee[]>([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState('all');
  const [selectedEmployee, setSelectedEmployee] = useState<KenyanEmployee | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [editData, setEditData] = useState<Partial<KenyanEmployee>>({});

  // Load employees with Kenyan payroll data
  useEffect(() => {
    loadEmployees();
  }, []);

  const loadEmployees = async () => {
    try {
      setLoading(true);
      
      // Get employees from database
      const employeeResponse = await employeeService.getEmployees();
      
      if (employeeResponse?.data?.length > 0) {
        // Get payroll data
        let payrollData: KenyanPayrollEmployee[] = [];
        try {
          payrollData = await PayrollDataService.getAllPayrollEmployees();
        } catch (error) {
          console.log('No payroll data available:', error);
        }
        
        // Merge employee and payroll data with Kenyan defaults
        const kenyanEmployees: KenyanEmployee[] = employeeResponse.data.map(employee => {
          const payrollEmployee = payrollData.find(pe => pe.employeeId === employee.employeeId);
          
          return {
            ...employee,
            payrollInfo: {
              employeeType: payrollEmployee?.payrollInfo?.employeeType || 'salaried',
              monthlySalary: payrollEmployee?.payrollInfo?.monthlySalary || employee.salary || 0,
              hourlyRate: payrollEmployee?.payrollInfo?.hourlyRate || 0,
              overtimeRate: payrollEmployee?.payrollInfo?.overtimeRate || 0,
              personalRelief: payrollEmployee?.payrollInfo?.personalRelief || 2400, // Kenyan personal relief
              kraPin: employee.taxInfo?.kraPin || '',
              nssfNumber: employee.taxInfo?.nssfNumber || '',
              nhifNumber: employee.taxInfo?.nhifNumber || '',
              bankAccount: {
                bankName: employee.bankDetails?.bankName || '',
                accountNumber: employee.bankDetails?.accountNumber || '',
                branchCode: employee.bankDetails?.branchCode || ''
              }
            }
          };
        });
        
        setEmployees(kenyanEmployees);
        setFilteredEmployees(kenyanEmployees);
      } else {
        setEmployees([]);
        setFilteredEmployees([]);
        toast({
          title: "No Employees Found",
          description: "No employees found in the database.",
          variant: "destructive"
        });
      }
    } catch (error) {
      console.error('Error loading employees:', error);
      toast({
        title: "Error",
        description: "Failed to load employee data",
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  // Filter employees
  useEffect(() => {
    let filtered = employees;

    if (searchTerm) {
      filtered = filtered.filter(emp => 
        emp.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        emp.employeeId.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    if (departmentFilter !== 'all') {
      filtered = filtered.filter(emp => emp.department === departmentFilter);
    }

    setFilteredEmployees(filtered);
  }, [employees, searchTerm, departmentFilter]);

  // Get unique departments
  const departments = ['all', ...new Set(employees.map(emp => emp.department).filter(Boolean))];

  // Calculate Kenyan net pay
  const calculateNetPay = (employee: KenyanEmployee): number => {
    const grossPay = employee.payrollInfo.employeeType === 'salaried' 
      ? employee.payrollInfo.monthlySalary || 0
      : (employee.payrollInfo.hourlyRate || 0) * 160; // Assume 160 hours/month

    const paye = PayrollEngine.calculatePAYE(grossPay, employee.payrollInfo.personalRelief);
    const nssf = PayrollEngine.calculateNSSF(grossPay);
    const nhif = PayrollEngine.calculateNHIF(grossPay);
    
    return grossPay - paye - nssf - nhif;
  };

  // Get gross pay
  const getGrossPay = (employee: KenyanEmployee): number => {
    return employee.payrollInfo.employeeType === 'salaried' 
      ? employee.payrollInfo.monthlySalary || 0
      : (employee.payrollInfo.hourlyRate || 0) * 160;
  };

  // Handle edit employee
  const handleEdit = (employee: KenyanEmployee) => {
    setSelectedEmployee(employee);
    setEditData(employee);
    setIsEditing(true);
  };

  // Save employee changes
  const handleSave = async () => {
    if (!selectedEmployee || !editData) return;

    try {
      // Update employee basic info
      await employeeService.updateEmployee(selectedEmployee.id, {
        firstName: editData.firstName,
        lastName: editData.lastName,
        department: editData.department,
        position: editData.position,
        salary: editData.payrollInfo?.monthlySalary,
        taxInfo: {
          kraPin: editData.payrollInfo?.kraPin,
          nssfNumber: editData.payrollInfo?.nssfNumber,
          nhifNumber: editData.payrollInfo?.nhifNumber
        },
        bankDetails: editData.payrollInfo?.bankAccount ? {
          bankName: editData.payrollInfo.bankAccount.bankName,
          accountNumber: editData.payrollInfo.bankAccount.accountNumber,
          branchCode: editData.payrollInfo.bankAccount.branchCode,
          accountType: 'savings'
        } : undefined
      });

      toast({
        title: "Success",
        description: "Employee updated successfully"
      });

      setIsEditing(false);
      setSelectedEmployee(null);
      setEditData({});
      await loadEmployees();
    } catch (error) {
      console.error('Error updating employee:', error);
      toast({
        title: "Error",
        description: "Failed to update employee",
        variant: "destructive"
      });
    }
  };

  if (loading) {
    return (
      <DashboardLayout title="Employee Compensation">
        <div className="flex items-center justify-center h-64">
          <RefreshCw className="h-6 w-6 animate-spin mr-2" />
          <span>Loading employees...</span>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout title="Employee Compensation">
      <div className="space-y-6">
        {/* Header */}
        {/* Removed duplicate title */}

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Employees</CardTitle>
              <Users className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">{employees.length}</div>
            </CardContent>
          </Card>
          
          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Monthly Payroll</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                KES {employees.reduce((sum, emp) => sum + getGrossPay(emp), 0).toLocaleString()}
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Average Net Pay</CardTitle>
              <DollarSign className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold">
                KES {employees.length > 0 
                  ? Math.round(employees.reduce((sum, emp) => sum + calculateNetPay(emp), 0) / employees.length).toLocaleString()
                  : '0'
                }
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Filters */}
        <Card>
          <CardHeader>
            <CardTitle>Employee Compensation Management</CardTitle>
            <CardDescription>View and manage employee compensation with Kenyan payroll calculations</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex flex-col sm:flex-row gap-4 mb-6">
              <div className="flex-1">
                <div className="relative">
                  <Search className="absolute left-2 top-2.5 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search employees..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-8"
                  />
                </div>
              </div>
              <Select value={departmentFilter} onValueChange={setDepartmentFilter}>
                <SelectTrigger className="w-full sm:w-[200px]">
                  <SelectValue placeholder="All Departments" />
                </SelectTrigger>
                <SelectContent>
                  {departments.map(dept => (
                    <SelectItem key={dept} value={dept}>
                      {dept === 'all' ? 'All Departments' : dept}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Employee Table */}
            {filteredEmployees.length === 0 ? (
              <Alert>
                <AlertTriangle className="h-4 w-4" />
                <AlertDescription>
                  No employees found. {searchTerm || departmentFilter !== 'all' ? 'Try adjusting your filters.' : 'Add employees to get started.'}
                </AlertDescription>
              </Alert>
            ) : (
              <div className="border rounded-lg">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Employee</TableHead>
                      <TableHead>Department</TableHead>
                      <TableHead>Position</TableHead>
                      <TableHead>Type</TableHead>
                      <TableHead>Gross Pay</TableHead>
                      <TableHead>Net Pay</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {filteredEmployees.map((employee) => (
                      <TableRow key={employee.id}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{employee.firstName} {employee.lastName}</div>
                            <div className="text-sm text-muted-foreground">{employee.employeeId}</div>
                          </div>
                        </TableCell>
                        <TableCell>{employee.department}</TableCell>
                        <TableCell>{employee.position}</TableCell>
                        <TableCell>
                          <Badge variant={employee.payrollInfo.employeeType === 'salaried' ? 'default' : 'secondary'}>
                            {employee.payrollInfo.employeeType === 'salaried' ? 'Salaried' : 'Hourly'}
                          </Badge>
                        </TableCell>
                        <TableCell>KES {getGrossPay(employee).toLocaleString()}</TableCell>
                        <TableCell>KES {calculateNetPay(employee).toLocaleString()}</TableCell>
                        <TableCell>
                          <Badge variant={employee.status === 'active' ? 'default' : 'secondary'}>
                            {employee.status}
                          </Badge>
                        </TableCell>
                        <TableCell>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => handleEdit(employee)}
                          >
                            <Edit className="h-4 w-4" />
                          </Button>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Edit Employee Dialog */}
        <Dialog open={isEditing} onOpenChange={(open) => {
          if (!open) {
            setIsEditing(false);
            setSelectedEmployee(null);
            setEditData({});
          }
        }}>
          <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Edit Employee Compensation</DialogTitle>
            </DialogHeader>
            
            {selectedEmployee && (
              <div className="space-y-6">
                {/* Basic Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Basic Information</h3>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label>First Name</Label>
                      <Input
                        value={editData.firstName || ''}
                        onChange={(e) => setEditData({...editData, firstName: e.target.value})}
                      />
                    </div>
                    <div>
                      <Label>Last Name</Label>
                      <Input
                        value={editData.lastName || ''}
                        onChange={(e) => setEditData({...editData, lastName: e.target.value})}
                      />
                    </div>
                    <div>
                      <Label>Department</Label>
                      <Input
                        value={editData.department || ''}
                        onChange={(e) => setEditData({...editData, department: e.target.value})}
                      />
                    </div>
                    <div>
                      <Label>Position</Label>
                      <Input
                        value={editData.position || ''}
                        onChange={(e) => setEditData({...editData, position: e.target.value})}
                      />
                    </div>
                  </div>
                </div>

                {/* Payroll Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Kenyan Payroll Information</h3>
                  
                  <div>
                    <Label>Employee Type</Label>
                    <Select
                      value={editData.payrollInfo?.employeeType || 'salaried'}
                      onValueChange={(value: 'salaried' | 'hourly') => setEditData({
                        ...editData,
                        payrollInfo: { ...editData.payrollInfo, employeeType: value }
                      })}
                    >
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="salaried">Salaried</SelectItem>
                        <SelectItem value="hourly">Hourly</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>

                  {editData.payrollInfo?.employeeType === 'salaried' ? (
                    <div>
                      <Label>Monthly Salary (KES)</Label>
                      <Input
                        type="number"
                        value={editData.payrollInfo?.monthlySalary || 0}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { ...editData.payrollInfo, monthlySalary: Number(e.target.value) }
                        })}
                      />
                    </div>
                  ) : (
                    <div>
                      <Label>Hourly Rate (KES)</Label>
                      <Input
                        type="number"
                        value={editData.payrollInfo?.hourlyRate || 0}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { ...editData.payrollInfo, hourlyRate: Number(e.target.value) }
                        })}
                      />
                    </div>
                  )}

                  <div>
                    <Label>Personal Relief (KES)</Label>
                    <Input
                      type="number"
                      value={editData.payrollInfo?.personalRelief || 2400}
                      onChange={(e) => setEditData({
                        ...editData,
                        payrollInfo: { ...editData.payrollInfo, personalRelief: Number(e.target.value) }
                      })}
                    />
                  </div>
                </div>

                {/* Kenyan Tax Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Kenyan Tax Information</h3>
                  <div className="grid grid-cols-1 gap-4">
                    <div>
                      <Label>KRA PIN</Label>
                      <Input
                        value={editData.payrollInfo?.kraPin || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { ...editData.payrollInfo, kraPin: e.target.value }
                        })}
                      />
                    </div>
                    <div>
                      <Label>NSSF Number</Label>
                      <Input
                        value={editData.payrollInfo?.nssfNumber || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { ...editData.payrollInfo, nssfNumber: e.target.value }
                        })}
                      />
                    </div>
                    <div>
                      <Label>NHIF Number</Label>
                      <Input
                        value={editData.payrollInfo?.nhifNumber || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { ...editData.payrollInfo, nhifNumber: e.target.value }
                        })}
                      />
                    </div>
                  </div>
                </div>

                {/* Bank Information */}
                <div className="space-y-4">
                  <h3 className="text-lg font-semibold">Bank Information</h3>
                  <div className="grid grid-cols-1 gap-4">
                    <div>
                      <Label>Bank Name</Label>
                      <Input
                        value={editData.payrollInfo?.bankAccount?.bankName || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { 
                            ...editData.payrollInfo, 
                            bankAccount: { 
                              ...editData.payrollInfo?.bankAccount, 
                              bankName: e.target.value 
                            }
                          }
                        })}
                      />
                    </div>
                    <div>
                      <Label>Account Number</Label>
                      <Input
                        value={editData.payrollInfo?.bankAccount?.accountNumber || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { 
                            ...editData.payrollInfo, 
                            bankAccount: { 
                              ...editData.payrollInfo?.bankAccount, 
                              accountNumber: e.target.value 
                            }
                          }
                        })}
                      />
                    </div>
                    <div>
                      <Label>Branch Code</Label>
                      <Input
                        value={editData.payrollInfo?.bankAccount?.branchCode || ''}
                        onChange={(e) => setEditData({
                          ...editData,
                          payrollInfo: { 
                            ...editData.payrollInfo, 
                            bankAccount: { 
                              ...editData.payrollInfo?.bankAccount, 
                              branchCode: e.target.value 
                            }
                          }
                        })}
                      />
                    </div>
                  </div>
                </div>

                {/* Actions */}
                <div className="flex justify-end space-x-2">
                  <Button variant="outline" onClick={() => setIsEditing(false)}>
                    Cancel
                  </Button>
                  <Button onClick={handleSave}>
                    <Save className="h-4 w-4 mr-2" />
                    Save Changes
                  </Button>
                </div>
              </div>
            )}
          </DialogContent>
        </Dialog>
      </div>
    </DashboardLayout>
  );
};

export default EmployeeCompensation;

import { useState, useEffect } from 'react';
import { DashboardLayout } from '@/components/layout/DashboardLayout';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { 
  Settings, 
  Save,
  RefreshCw,
  Building,
  Calendar,
  Clock,
  DollarSign,
  FileText,
  Users,
  Info,
  CheckCircle,
  AlertTriangle,
  Shield
} from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { api } from '@/services/unifiedApi';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';

const PayrollSettings = () => {
  const { toast } = useToast();
  const [isSaving, setIsSaving] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [validationErrors, setValidationErrors] = useState<Record<string, string>>({});
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const [showConfirmDialog, setShowConfirmDialog] = useState(false);
  const [originalSettings, setOriginalSettings] = useState<any>(null);
  
  const [settings, setSettings] = useState({
    companyInfo: {
      name: "",
      kraPin: "",
      nssfNumber: "",
      nhifNumber: "",
      address: "",
      postalCode: "",
      city: ""
    },
    payrollDefaults: {
      personalRelief: 0,
      overtimeMultiplier: 0,
      workingDaysPerMonth: 0,
      workingHoursPerDay: 0,
      payFrequency: "",
      payDay: 0,
      cutoffDay: 0
    },
    approvalWorkflow: {
      requirePayrollApproval: false,
      approvalLevels: 0,
      autoApproveThreshold: 0,
      notifyEmployeesOnPayment: false,
      sendPayslipsByEmail: false
    },
    bankingInfo: {
      bankName: "",
      accountNumber: "",
      branchCode: "",
      swiftCode: ""
    }
  });

  // Load settings on component mount
  useEffect(() => {
    loadSettings();
  }, []);

  const loadSettings = async () => {
    setIsLoading(true);
    try {
      const loadedSettings = await api.payroll.getPayrollSettings();
      
      // Use only real data from API, no hardcoded fallbacks
      const settingsData = loadedSettings as any || {};
      
      // Map API data directly without hardcoded defaults
      const completeSettings = {
        companyInfo: {
          name: settingsData?.companyInfo?.name || "",
          kraPin: settingsData?.companyInfo?.kraPin || "",
          nssfNumber: settingsData?.companyInfo?.nssfNumber || "",
          nhifNumber: settingsData?.companyInfo?.nhifNumber || "",
          address: settingsData?.companyInfo?.address || "",
          postalCode: settingsData?.companyInfo?.postalCode || "",
          city: settingsData?.companyInfo?.city || ""
        },
        payrollDefaults: {
          personalRelief: settingsData?.payrollDefaults?.personalRelief || 0,
          overtimeMultiplier: settingsData?.payrollDefaults?.overtimeMultiplier || 0,
          workingDaysPerMonth: settingsData?.payrollDefaults?.workingDaysPerMonth || 0,
          workingHoursPerDay: settingsData?.payrollDefaults?.workingHoursPerDay || 0,
          payFrequency: settingsData?.payrollDefaults?.payFrequency || "",
          payDay: settingsData?.payrollDefaults?.payDay || 0,
          cutoffDay: settingsData?.payrollDefaults?.cutoffDay || 0
        },
        approvalWorkflow: {
          requirePayrollApproval: settingsData?.approvalWorkflow?.requirePayrollApproval ?? false,
          approvalLevels: settingsData?.approvalWorkflow?.approvalLevels || 0,
          autoApproveThreshold: settingsData?.approvalWorkflow?.autoApproveThreshold || 0,
          notifyEmployeesOnPayment: settingsData?.approvalWorkflow?.notifyEmployeesOnPayment ?? false,
          sendPayslipsByEmail: settingsData?.approvalWorkflow?.sendPayslipsByEmail ?? false
        },
        bankingInfo: {
          bankName: settingsData?.bankingInfo?.bankName || "",
          accountNumber: settingsData?.bankingInfo?.accountNumber || "",
          branchCode: settingsData?.bankingInfo?.branchCode || "",
          swiftCode: settingsData?.bankingInfo?.swiftCode || ""
        }
      };
      
      setSettings(completeSettings);
      setOriginalSettings(JSON.parse(JSON.stringify(completeSettings)));
      setHasUnsavedChanges(false);
      
      toast({
        title: "Settings Loaded",
        description: "Payroll settings loaded successfully from database.",
        variant: "default"
      });
    } catch (error) {
      console.error('Failed to load settings:', error);
      toast({
        title: "Error Loading Settings",
        description: "Failed to load payroll settings from database. Please try again.",
        variant: "destructive"
      });
      
      // Use empty settings structure if loading fails - no hardcoded data
      const emptySettings = {
        companyInfo: {
          name: "",
          kraPin: "",
          nssfNumber: "",
          nhifNumber: "",
          address: "",
          postalCode: "",
          city: ""
        },
        payrollDefaults: {
          personalRelief: 0,
          overtimeMultiplier: 0,
          workingDaysPerMonth: 0,
          workingHoursPerDay: 0,
          payFrequency: "",
          payDay: 0,
          cutoffDay: 0
        },
        approvalWorkflow: {
          requirePayrollApproval: false,
          approvalLevels: 0,
          autoApproveThreshold: 0,
          notifyEmployeesOnPayment: false,
          sendPayslipsByEmail: false
        },
        bankingInfo: {
          bankName: "",
          accountNumber: "",
          branchCode: "",
          swiftCode: ""
        }
      };
      setSettings(emptySettings);
      setOriginalSettings(JSON.parse(JSON.stringify(emptySettings)));
    } finally {
      setIsLoading(false);
    }
  };

  // Validation functions
  const validateKRAPin = (pin: string): boolean => {
    // KRA PIN format: P051234567A (P + 9 digits + letter)
    const kraRegex = /^P\d{9}[A-Z]$/;
    return kraRegex.test(pin);
  };

  const validateNSSFNumber = (number: string): boolean => {
    // NSSF format: NSSF + 6 digits
    const nssfRegex = /^NSSF\d{6}$/;
    return nssfRegex.test(number);
  };

  const validateNHIFNumber = (number: string): boolean => {
    // NHIF format: NHIF + 6 digits
    const nhifRegex = /^NHIF\d{6}$/;
    return nhifRegex.test(number);
  };

  const validateBankAccount = (account: string): boolean => {
    // Bank account: 10-16 digits
    const accountRegex = /^\d{10,16}$/;
    return accountRegex.test(account);
  };

  const validateSwiftCode = (swift: string): boolean => {
    // SWIFT code: 8 or 11 characters
    const swiftRegex = /^[A-Z]{6}[A-Z0-9]{2}([A-Z0-9]{3})?$/;
    return swiftRegex.test(swift);
  };

  const validateSettings = (): boolean => {
    const errors: Record<string, string> = {};

    // Company Info Validation
    if (!settings.companyInfo.name.trim()) {
      errors.name = "Company name is required";
    }

    if (settings.companyInfo.kraPin && !validateKRAPin(settings.companyInfo.kraPin)) {
      errors.kraPin = "Invalid KRA PIN format (e.g., P051234567A)";
    }

    if (settings.companyInfo.nssfNumber && !validateNSSFNumber(settings.companyInfo.nssfNumber)) {
      errors.nssfNumber = "Invalid NSSF number format (e.g., NSSF001234)";
    }

    if (settings.companyInfo.nhifNumber && !validateNHIFNumber(settings.companyInfo.nhifNumber)) {
      errors.nhifNumber = "Invalid NHIF number format (e.g., NHIF001234)";
    }

    if (!settings.companyInfo.address.trim()) {
      errors.address = "Address is required";
    }

    if (!settings.companyInfo.city.trim()) {
      errors.city = "City is required";
    }

    // Payroll Defaults Validation
    if (settings.payrollDefaults.personalRelief < 0) {
      errors.personalRelief = "Personal relief cannot be negative";
    }

    if (settings.payrollDefaults.overtimeMultiplier < 1) {
      errors.overtimeMultiplier = "Overtime multiplier must be at least 1.0";
    }

    if (settings.payrollDefaults.workingDaysPerMonth < 1 || settings.payrollDefaults.workingDaysPerMonth > 31) {
      errors.workingDaysPerMonth = "Working days must be between 1 and 31";
    }

    if (settings.payrollDefaults.workingHoursPerDay < 1 || settings.payrollDefaults.workingHoursPerDay > 24) {
      errors.workingHoursPerDay = "Working hours must be between 1 and 24";
    }

    if (!settings.payrollDefaults.payFrequency) {
      errors.payFrequency = "Pay frequency is required";
    }

    if (settings.payrollDefaults.payDay < 1 || settings.payrollDefaults.payDay > 31) {
      errors.payDay = "Pay day must be between 1 and 31";
    }

    // Banking Info Validation (only if provided)
    if (settings.bankingInfo.bankName && !settings.bankingInfo.bankName.trim()) {
      errors.bankName = "Bank name cannot be empty if provided";
    }

    if (settings.bankingInfo.accountNumber && !validateBankAccount(settings.bankingInfo.accountNumber)) {
      errors.accountNumber = "Invalid account number (10-16 digits)";
    }

    if (settings.bankingInfo.swiftCode && !validateSwiftCode(settings.bankingInfo.swiftCode)) {
      errors.swiftCode = "Invalid SWIFT code format";
    }

    setValidationErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSaveSettings = async () => {
    if (!validateSettings()) {
      toast({
        title: "Validation Error",
        description: "Please fix the validation errors before saving.",
        variant: "destructive"
      });
      return;
    }

    setIsSaving(true);
    
    try {
      // Real API call to save settings
      await api.payroll.updatePayrollSettings(settings);
      
      setOriginalSettings(JSON.parse(JSON.stringify(settings)));
      setHasUnsavedChanges(false);
      setShowConfirmDialog(false);
      
      toast({
        title: "Settings Saved",
        description: "Payroll settings have been updated successfully.",
        variant: "default"
      });
    } catch (error: any) {
      console.error('Failed to save settings:', error);
      
      let errorMessage = "Failed to save settings. Please try again.";
      
      // Handle specific error types
      if (error.message?.includes('network')) {
        errorMessage = "Network error. Please check your connection and try again.";
      } else if (error.message?.includes('unauthorized')) {
        errorMessage = "You don't have permission to update these settings.";
      } else if (error.message?.includes('validation')) {
        errorMessage = "Server validation failed. Please check your inputs.";
      }
      
      toast({
        title: "Error Saving Settings",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setIsSaving(false);
    }
  };

  const handleInputChange = (section: string, field: string, value: any) => {
    setSettings(prev => {
      // Validate and sanitize numeric values to prevent NaN
      let sanitizedValue = value;
      
      // Handle number inputs specifically
      if (section === 'payrollDefaults') {
        if (field === 'personalRelief' || field === 'overtimeMultiplier' || field === 'autoApproveThreshold') {
          const numValue = parseFloat(value);
          sanitizedValue = isNaN(numValue) || value === '' ? 0 : numValue;
        } else if (field === 'workingDaysPerMonth' || field === 'workingHoursPerDay' || field === 'payDay' || field === 'cutoffDay' || field === 'approvalLevels') {
          const intValue = parseInt(value);
          sanitizedValue = isNaN(intValue) || value === '' ? 0 : intValue;
        }
      }
      
      const newSettings = {
        ...prev,
        [section]: {
          ...prev[section as keyof typeof prev],
          [field]: sanitizedValue
        }
      };
      
      // Check if settings have changed
      const hasChanges = JSON.stringify(newSettings) !== JSON.stringify(originalSettings);
      setHasUnsavedChanges(hasChanges);
      
      // Clear validation error for this field
      if (validationErrors[field]) {
        setValidationErrors(prev => {
          const newErrors = { ...prev };
          delete newErrors[field];
          return newErrors;
        });
      }
      
      return newSettings;
    });
  };

  const handleConfirmSave = () => {
    setShowConfirmDialog(true);
  };

  const getChangedFields = () => {
    if (!originalSettings) return [];
    
    const changes: string[] = [];
    const checkSection = (sectionName: string, original: any, current: any) => {
      Object.keys(current).forEach(key => {
        if (original[key] !== current[key]) {
          changes.push(`${sectionName}.${key}`);
        }
      });
    };
    
    checkSection('Company Info', originalSettings.companyInfo, settings.companyInfo);
    checkSection('Payroll Defaults', originalSettings.payrollDefaults, settings.payrollDefaults);
    checkSection('Approval Workflow', originalSettings.approvalWorkflow, settings.approvalWorkflow);
    checkSection('Banking Info', originalSettings.bankingInfo, settings.bankingInfo);
    
    return changes;
  };

  if (isLoading) {
    return (
      <DashboardLayout title="Payroll Settings">
        <div className="flex items-center justify-center h-64">
          <RefreshCw className="h-8 w-8 animate-spin text-blue-500" />
          <span className="ml-2">Loading settings...</span>
        </div>
      </DashboardLayout>
    );
  }

  // Safety check for settings object
  if (!settings || !settings.companyInfo || !settings.payrollDefaults || !settings.approvalWorkflow || !settings.bankingInfo) {
    return (
      <DashboardLayout title="Payroll Settings">
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <AlertTriangle className="h-8 w-8 text-yellow-500 mx-auto mb-4" />
            <p className="text-lg">Settings not available</p>
            <p className="text-sm text-gray-500 mt-2">Please refresh the page or contact support.</p>
            <Button onClick={loadSettings} className="mt-4">
              <RefreshCw className="h-4 w-4 mr-2" />
              Retry Loading
            </Button>
          </div>
        </div>
      </DashboardLayout>
    );
  }

  return (
    <DashboardLayout title="Payroll Settings">
      <div className="space-y-6">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Payroll System Settings</CardTitle>
                <CardDescription>
                  Configure payroll calculation parameters and company information
                </CardDescription>
              </div>
              <Button onClick={handleSaveSettings} disabled={isSaving}>
                {isSaving ? (
                  <>
                    <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save Settings
                  </>
                )}
              </Button>
            </div>
          </CardHeader>
        </Card>

        {hasUnsavedChanges && (
          <Alert>
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription>
              You have unsaved changes. Don't forget to save your settings.
            </AlertDescription>
          </Alert>
        )}

        <Tabs defaultValue="company" className="space-y-6">
          <TabsList>
            <TabsTrigger value="company">Company Information</TabsTrigger>
            <TabsTrigger value="payroll">Payroll Defaults</TabsTrigger>
            <TabsTrigger value="workflow">Approval Workflow</TabsTrigger>
            <TabsTrigger value="banking">Banking Information</TabsTrigger>
          </TabsList>

          <TabsContent value="company" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Building className="h-5 w-5 mr-2" />
                  Company Information
                </CardTitle>
                <CardDescription>
                  Company details used in payroll reports and statutory filings
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid gap-6 md:grid-cols-2">
                  <div className="space-y-2">
                    <Label htmlFor="company-name">Company Name</Label>
                    <Input 
                      id="company-name" 
                      value={settings.companyInfo.name}
                      onChange={(e) => handleInputChange('companyInfo', 'name', e.target.value)}
                      className={validationErrors.name ? 'border-red-500' : ''}
                      placeholder="Enter your company name"
                    />
                    {validationErrors.name && (
                      <p className="text-sm text-red-500">{validationErrors.name}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="kra-pin">KRA PIN</Label>
                    <Input 
                      id="kra-pin" 
                      value={settings.companyInfo.kraPin}
                      onChange={(e) => handleInputChange('companyInfo', 'kraPin', e.target.value)}
                      className={validationErrors.kraPin ? 'border-red-500' : ''}
                      placeholder="e.g., P051234567A"
                    />
                    {validationErrors.kraPin && (
                      <p className="text-sm text-red-500">{validationErrors.kraPin}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="nssf-number">NSSF Number</Label>
                    <Input 
                      id="nssf-number" 
                      value={settings.companyInfo.nssfNumber}
                      onChange={(e) => handleInputChange('companyInfo', 'nssfNumber', e.target.value)}
                      className={validationErrors.nssfNumber ? 'border-red-500' : ''}
                      placeholder="e.g., NSSF001234"
                    />
                    {validationErrors.nssfNumber && (
                      <p className="text-sm text-red-500">{validationErrors.nssfNumber}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="nhif-number">NHIF Number</Label>
                    <Input 
                      id="nhif-number" 
                      value={settings.companyInfo.nhifNumber}
                      onChange={(e) => handleInputChange('companyInfo', 'nhifNumber', e.target.value)}
                      className={validationErrors.nhifNumber ? 'border-red-500' : ''}
                      placeholder="e.g., NHIF001234"
                    />
                    {validationErrors.nhifNumber && (
                      <p className="text-sm text-red-500">{validationErrors.nhifNumber}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="address">Address</Label>
                    <Input 
                      id="address" 
                      value={settings.companyInfo.address}
                      onChange={(e) => handleInputChange('companyInfo', 'address', e.target.value)}
