                      </SelectContent>
                    </Select>
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="pay-day">Pay Day</Label>
                    <Input 
                      id="pay-day" 
                      type="number"
                      min="1"
                      max="31"
                      value={settings.payrollDefaults.payDay || ''}
                      onChange={(e) => handleInputChange('payrollDefaults', 'payDay', e.target.value)}
                      className={validationErrors.payDay ? 'border-red-500' : ''}
                      placeholder="e.g., 25"
                    />
                    {validationErrors.payDay && (
                      <p className="text-sm text-red-500">{validationErrors.payDay}</p>
                    )}
                    <p className="text-sm text-muted-foreground">
                      Day of month when employees are paid
                    </p>
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="workflow" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Users className="h-5 w-5 mr-2" />
                  Approval Workflow
                </CardTitle>
                <CardDescription>
                  Configure payroll approval process and notifications
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-6">
                  <div className="flex items-center justify-between">
                    <div className="space-y-0.5">
                      <Label htmlFor="require-approval">Require Payroll Approval</Label>
                      <p className="text-sm text-muted-foreground">
                        Require manager approval before processing payroll
                      </p>
                    </div>
                    <Switch 
                      id="require-approval"
                      checked={settings.approvalWorkflow.requirePayrollApproval}
                      onCheckedChange={(checked) => handleInputChange('approvalWorkflow', 'requirePayrollApproval', checked)}
                    />
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="approval-levels">Approval Levels</Label>
                    <Select 
                      value={settings.approvalWorkflow.approvalLevels.toString()}
                      onValueChange={(value) => handleInputChange('approvalWorkflow', 'approvalLevels', parseInt(value))}
                      disabled={!settings.approvalWorkflow.requirePayrollApproval}
                    >
                      <SelectTrigger id="approval-levels">
                        <SelectValue placeholder="Select approval levels" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="1">1 Level</SelectItem>
                        <SelectItem value="2">2 Levels</SelectItem>
                        <SelectItem value="3">3 Levels</SelectItem>
                      </SelectContent>
                    </Select>
                    <p className="text-sm text-muted-foreground">
                      Number of approval levels required
                    </p>
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="auto-approve-threshold">Auto-Approve Threshold (KES)</Label>
                    <Input 
                      id="auto-approve-threshold" 
                      type="number"
                      value={settings.approvalWorkflow.autoApproveThreshold}
                      onChange={(e) => handleInputChange('approvalWorkflow', 'autoApproveThreshold', parseInt(e.target.value))}
                      disabled={!settings.approvalWorkflow.requirePayrollApproval}
                    />
                    <p className="text-sm text-muted-foreground">
                      Automatically approve changes below this amount
                    </p>
                  </div>
                  
                  <div className="flex items-center justify-between">
                    <div className="space-y-0.5">
                      <Label htmlFor="notify-employees">Notify Employees on Payment</Label>
                      <p className="text-sm text-muted-foreground">
                        Send email notification when payment is processed
                      </p>
                    </div>
                    <Switch 
                      id="notify-employees"
                      checked={settings.approvalWorkflow.notifyEmployeesOnPayment}
                      onCheckedChange={(checked) => handleInputChange('approvalWorkflow', 'notifyEmployeesOnPayment', checked)}
                    />
                  </div>
                  
                  <div className="flex items-center justify-between">
                    <div className="space-y-0.5">
                      <Label htmlFor="send-payslips">Send Payslips by Email</Label>
                      <p className="text-sm text-muted-foreground">
                        Automatically email payslips to employees
                      </p>
                    </div>
                    <Switch 
                      id="send-payslips"
                      checked={settings.approvalWorkflow.sendPayslipsByEmail}
                      onCheckedChange={(checked) => handleInputChange('approvalWorkflow', 'sendPayslipsByEmail', checked)}
                    />
                  </div>
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="banking" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <DollarSign className="h-5 w-5 mr-2" />
                  Banking Information
                </CardTitle>
                <CardDescription>
                  Company banking details for payroll processing
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid gap-6 md:grid-cols-2">
                  <div className="space-y-2">
                    <Label htmlFor="bank-name">Bank Name</Label>
                    <Input 
                      id="bank-name" 
                      value={settings.bankingInfo.bankName}
                      onChange={(e) => handleInputChange('bankingInfo', 'bankName', e.target.value)}
                      className={validationErrors.bankName ? 'border-red-500' : ''}
                      placeholder="e.g., Kenya Commercial Bank"
                    />
                    {validationErrors.bankName && (
                      <p className="text-sm text-red-500">{validationErrors.bankName}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="account-number">Account Number</Label>
                    <Input 
                      id="account-number" 
                      value={settings.bankingInfo.accountNumber}
                      onChange={(e) => handleInputChange('bankingInfo', 'accountNumber', e.target.value)}
                      className={validationErrors.accountNumber ? 'border-red-500' : ''}
                      placeholder="e.g., 1234567890"
                    />
                    {validationErrors.accountNumber && (
                      <p className="text-sm text-red-500">{validationErrors.accountNumber}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="branch-code">Branch Code</Label>
                    <Input 
                      id="branch-code" 
                      value={settings.bankingInfo.branchCode}
                      onChange={(e) => handleInputChange('bankingInfo', 'branchCode', e.target.value)}
                      className={validationErrors.branchCode ? 'border-red-500' : ''}
                    />
                    {validationErrors.branchCode && (
                      <p className="text-sm text-red-500">{validationErrors.branchCode}</p>
                    )}
                  </div>
                  
                  <div className="space-y-2">
                    <Label htmlFor="swift-code">SWIFT Code</Label>
                    <Input 
                      id="swift-code" 
                      value={settings.bankingInfo.swiftCode}
                      onChange={(e) => handleInputChange('bankingInfo', 'swiftCode', e.target.value)}
                      className={validationErrors.swiftCode ? 'border-red-500' : ''}
                    />
                    {validationErrors.swiftCode && (
                      <p className="text-sm text-red-500">{validationErrors.swiftCode}</p>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>

            <Alert>
              <Info className="h-4 w-4" />
              <AlertDescription>
                Banking information is used for payroll processing and will appear on bank transfer files.
              </AlertDescription>
            </Alert>
          </TabsContent>
        </Tabs>

        <Card>
          <CardContent className="pt-6">
            <div className="flex justify-between items-center">
              <div className="flex items-center text-sm text-muted-foreground">
                <Clock className="h-4 w-4 mr-2" />
                <span>Last updated: July 1, 2025 at 10:23 AM</span>
              </div>
              <Button onClick={handleSaveSettings} disabled={isSaving}>
                {isSaving ? (
                  <>
                    <RefreshCw className="h-4 w-4 mr-2 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save Settings
                  </>
                )}
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </DashboardLayout>
  );
};

export default PayrollSettings;

/* eslint-disable @typescript-eslint/no-explicit-any */

import { 
  TeamMemberAllocation, 
  PlannedRole, 
  User, 
  Role,
  LeaveRequest, 
  SalaryAdvanceRequest, 
  WorkflowStep, 
  PayrollDeduction,
  AdvancePayrollIntegration 
} from "@/types/types";

// Import Kenyan payroll types
import type {
  TimeEntry,
  PayrollPeriod,
  KenyanPayrollEmployee as PayrollEmployee,
  KenyanPayrollRecord as PayrollRecord,
  KenyanPayStub as PayStub,
  KenyanTaxTable as TaxTable,
  KenyanPayrollReport as PayrollReport,
  KenyanComplianceReport as ComplianceReport,
  KenyanPayrollAuditLog as PayrollAuditLog,
  KenyanPayrollSettings as PayrollSettings
} from "@/types/payroll";

// Import real API services
import { authService } from './api/auth.service';
import { employeeService } from './api/employee.service';
import { leaveService } from './api/leave.service';
import { salaryAdvanceService } from './api/salaryAdvance.service';
import { payrollService } from './api/payroll.service';

// Helper function to map backend employee to frontend User
const mapEmployeeToUser = (employee: any): User => ({
  id: employee.id,
  firstName: employee.firstName,
  lastName: employee.lastName,
  email: employee.email,
  role: (employee.position || 'EMPLOYEE') as 'ADMIN' | 'HR_MANAGER' | 'OPERATIONS_MANAGER' | 'EMPLOYEE',
  department: employee.department,
  status: employee.status === 'active' ? 'active' : 'inactive',
  position: employee.position,
  employeeId: employee.employeeId,
  branchId: employee.branchId,
  departmentId: employee.departmentId
});

// Helper function to map backend leave request to frontend LeaveRequest
const mapBackendLeaveToFrontend = (backendLeave: any): LeaveRequest => ({
  id: backendLeave.id,
  employeeId: backendLeave.employeeId,
  employeeName: `${backendLeave.employee.firstName} ${backendLeave.employee.lastName}`,
  branch: backendLeave.employee.department,
  leaveType: backendLeave.leaveType.name,
  startDate: backendLeave.startDate,
  endDate: backendLeave.endDate,
  days: backendLeave.totalDays,
  reason: backendLeave.reason,
  status: mapLeaveStatus(backendLeave.status),
  submissionDate: backendLeave.appliedDate,
  currentStep: mapLeaveCurrentStep(backendLeave.status),
  workflowHistory: [
    {
      id: `wf-${backendLeave.id}`,
      step: 'submission',
      actor: backendLeave.employeeId,
      actorName: `${backendLeave.employee.firstName} ${backendLeave.employee.lastName}`,
      action: 'submitted',
      timestamp: backendLeave.createdAt,
      comments: backendLeave.reason
    }
  ]
});

// Helper function to map backend salary advance to frontend SalaryAdvanceRequest
const mapBackendSalaryAdvanceToFrontend = (backendAdvance: any): SalaryAdvanceRequest => ({
  id: parseInt(backendAdvance.id),
  employeeId: parseInt(backendAdvance.employeeId),
  employeeName: backendAdvance.employee ? `${backendAdvance.employee.firstName} ${backendAdvance.employee.lastName}` : 'Unknown Employee',
  branch: backendAdvance.employee?.department || 'Unknown',
  amount: backendAdvance.requestedAmount || 0,
  reason: backendAdvance.reason,
  requestDate: backendAdvance.requestDate || backendAdvance.createdAt?.split('T')[0],
  status: mapSalaryAdvanceStatus(backendAdvance.status),
  disbursementMethod: 'bank_transfer',
  currentStep: mapSalaryAdvanceCurrentStep(backendAdvance.status),
  payrollIntegration: {
    payrollDeductionId: `PD-${backendAdvance.id}`,
    monthlyDeduction: Math.round((backendAdvance.requestedAmount || 0) / 6),
    repaymentMonths: 6,
    startDeductionDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    estimatedCompletionDate: new Date(Date.now() + 6 * 30 * 24 * 60 * 60 * 1000).toISOString().split('T')[0],
    deductionPriority: 1
  },
  repaymentDetails: {
    originalAmount: backendAdvance.requestedAmount || 0,
    totalDeducted: 0,
    remainingBalance: backendAdvance.requestedAmount || 0,
    repaymentMethod: 'payroll_deduction_only',
    deductionHistory: []
  },
  workflowHistory: [
    {
      id: `wf-sa-${backendAdvance.id}`,
      step: 'submission',
      actor: backendAdvance.employeeId,
      actorName: backendAdvance.employee ? `${backendAdvance.employee.firstName} ${backendAdvance.employee.lastName}` : 'Unknown Employee',
      action: 'submitted',
      timestamp: backendAdvance.createdAt,
      comments: backendAdvance.reason
    }
  ]
});

// Status mapping functions
const mapLeaveStatus = (backendStatus: string): string => {
  switch (backendStatus) {
    case 'pending': return 'pending_ops_initial';
    case 'approved': return 'ops_final_approved';
    case 'rejected': return 'ops_final_rejected';
    default: return backendStatus;
  }
};

const mapLeaveCurrentStep = (backendStatus: string): string => {
  switch (backendStatus) {
    case 'pending': return 'ops_initial';
    case 'approved': 
    case 'rejected': return 'completed';
    default: return 'ops_initial';
  }
};

const mapSalaryAdvanceStatus = (backendStatus: string): string => {
  switch (backendStatus) {
    case 'pending': return 'pending_ops_initial';
    case 'approved': return 'ops_final_approved';
    case 'rejected': return 'ops_final_rejected';
    case 'disbursed': return 'disbursed';
    default: return backendStatus;
  }
};

const mapSalaryAdvanceCurrentStep = (backendStatus: string): string => {
  switch (backendStatus) {
    case 'pending': return 'ops_initial';
    case 'approved': return 'disbursement';
    case 'disbursed': return 'repayment';
    case 'rejected': return 'completed';
    default: return 'ops_initial';
  }
};

// Create a unified API that maps to real API services
export const api = {
  // Auth methods
  auth: {
    getToken: async () => {
      try {
        // Cleaned up: removed legacy mock token comment
        return "mock-auth-token-" + Math.random().toString(36).substring(2);
      } catch (error) {
        console.error("Auth.getToken error:", error);
        throw error;
      }
    },
    isAuthenticated: async () => {
      try {
        return authService.isAuthenticated();
      } catch (error) {
        console.error("Auth.isAuthenticated error:", error);
        return false;
      }
    }
  },
  
